css:
@-moz-document url-prefix("https://app.everhour.com/") {
body {
    background-color: #333;
    color: #fff;
}

.header {
    background-color: #222 !important;
    border-bottom-color: #111 !important;
}

.content {
    background-color: #333;
    border: 1px solid #111;
}

#beacon-container {
	display: none;
}

a {
	color: #fff !important;
}

.button-primary,
.button-success {
    background-color: #f03;
    border-color: #f03;
}

.button-success:hover {
	background-color: #3232dc !important;
}

.time-page-tabs-tab-active {
    border-bottom-color: #f03 !important;
}

.time-week-dots-dot.normal {
	background-color: #f03 !important;
}

.timesheet-row-header .timesheet-column-today-marker, 
.timesheet-row-summary .timesheet-column-today-marker {
	background-color: #f03 !important;
}

.c-FabButton__icon--icon {
	background-color: #f03 !important;
    border-radius: 50%;
}

.header-navigation-list-item-active > a span {
	border-bottom-width: 0 !important;
}

.header-navigation-list-item-active > a{
    color: #fff !important;
}

.header-navigation-list-item-active.header-navigation-list-item {
	background-color: #f03 !important;
}

.header-navigation-list-item:hover {
	background-color: #f03 !important;
}

.header-navigation-list-item > a:hover {
	color: #fff !important;
}

h1 {
	color: #fff;
}

h2 {
	color: #fff !important;
}

h3 {
    color: #fff;
}

.time-input .form-inline>div.search .placeholder,
.time-input .form-inline>label.search .placeholder {
    color: #fff !important;
}

.time-week-header:hover {
    background-color: #444 !important;
}

.timesheet-row-header {
    background-color: #444 !important;
}

.timesheet-row-summary {
    background-color: #444 !important;
    color: #fff !important;
}

.timesheet-row-header .timesheet-column-day-weekday,
.timesheet-row-header .timesheet-column-total {
    color: #ccc;
}

.timelist-row-wrapper .task .task-name,
.timelist-row-wrapper .timelist-row-task .task-name,
a.link-text {
    color: #fff;
}

.timelist-day-header {
    background-color: #444 !important;
    border-bottom-color: #111 !important;
}

.timesheet-body timesheet-row-footer {
	background-color: #222 !important;
}

.outer-component.time-input-selection .callout-warning {
	background-color: #444 !important;
    border-top: 1px solid #555 !important;
    border-right: 1px solid #555 !important;
    border-bottom: 1px solid #555 !important;
}

.timesheet-row.timesheet-row-body,
timesheet-row-task > :nth-of-type(even) .timesheet-row {
    background-color: #3a3a3a !important;
}
    
.timelist-day-relative-date {
    color: #ddd !important;
}

.timelist-day-month-date {
    color: #aaa !important;
}

figure.project {
    color: #ccc;
}


.timesheet-column-today {
    border-left: 1px dotted #111;
    border-right: 1px dotted #111;
}

.timesheet-column {
    border-right: 1px dotted #111;
}

.timesheet-row-header {
    border-top: 1px solid #111;
}

.timesheet-row {
    border-bottom: 1px solid #111 !important;
}

.timesheet-column-task,
.timesheet-column-user {
    border-right: 1px dotted #111;
}

.timesheet-row-header:hover,
.timesheet-row-summary:hover {
    background-color: #555 !important;
}

.timesheet-row-header .timesheet-column-day-date {
	color: #aaa;
}

.timesheet-row-header:hover .timesheet-column-total {
	background-color: #111;
}

.time-input .form-inline>div.comment input,.time-input .form-inline>div.date input, 
.time-input .form-inline>div.time input, .time-input .form-inline>label.comment input, 
.time-input .form-inline>label.date input,.time-input .form-inline>label.time input {
	background-color: #333 !important;
}

.time-input .form-inline>div.time, 
.time-input .form-inline>label.time {
    border-left: 1px solid #111 !important;
}

.time-input .form-inline>div.date, 
.time-input .form-inline>label.date {
    border-left: 1px solid #111 !important;
    border-right: 1px solid #111 !important;
}


.popover-body {
    background: #333;
}

.popover {
    background: #444;
    border: 1px solid #111;
}

.popover:after {
    border-bottom-color: #444;
}

.popover:before {
    border-bottom-color: #444;
}

.list-row {
    border-color: #111 !important;
}

.list-row:hover {
	background-color: #555 !important;
}


.application-loading:not(.inner) {
    background: #333 !important;
    border-color: transparent !important;
}

.application-loading {
	color: #fff;
}

quick-summary-widget .quick-summary-widget-wrapper {
    background-color: #333 !important;
    border: 1px solid #111 !important;
}

.page-widget-content {
    border: 1px solid #111;
}

.page-widget-table-header .page-widget-table-cell {
    background: #333 !important;
    border-bottom: 1px solid #111 !important;
}

.page-widget-table-body .page-widget-table-cell-day, 
.page-widget-table-body .page-widget-table-cell-week, 
.page-widget-table-header .page-widget-table-cell-day, 
.page-widget-table-header .page-widget-table-cell-week {
    border-right-color: #111 !important;
}

.page-dropdown-button.opened {
    background: #444;
}

.page-dropdown-button {
	border-color: transparent;
    color: #fff;
}

icon-arrow {
	background-color: #fff;
}

.icon-arrow {
	color: #fff;
}

.page-manager {
    background: #444;
}
    
.page-tabs {
    border-bottom-color: #111;   
}

.form-control {
    background-color: #555;
    border-color: transparent;
    color: #fff;
}

.badge {
    border-color: #444;   
}

.page-tabs-container .tab.active {
    color: #24be6a;
}

.service-error {
    background-color: #333 !important;
    border-color: transparent !important;
}

.notifications-box .item a {
    color: #fff !important;
}

.notifications-box .date > span {
    background-color: transparent !important;
}

timesheet-row-footer {
	background-color: #222;
}

time-load-more-button .wrapper {
	border-top-color: transparent !important;
}

my-timesheet-week {
	border-bottom-color: transparent !important;
}

.time-input .form-inline>div.comment input::-webkit-input-placeholder, 
.time-input .form-inline>div.date input::-webkit-input-placeholder, 
.time-input .form-inline>div.time input::-webkit-input-placeholder, 
.time-input .form-inline>label.comment input::-webkit-input-placeholder, 
.time-input .form-inline>label.date input::-webkit-input-placeholder,
.time-input .form-inline>label.time input::-webkit-input-placeholder {
    background-color: #333 !important;
    color: #fff !important;
}

.time-page-tabs {
	border-bottom-color: #111 !important;
}

.badge-pill-bordered {
	border-color: #f03;
}

.application-loading-spinner > div {
	background-color: #f03;
}

 .time-input search-container>div.input input {
    background-color: #333 !important;
}

.time-input search-container .items,
.time-input search-container .nothing-found {
    background-color: #333  !important;
  	border-color: #111 !important;
}

  input {
  	color: #fff !important;
  }
  
.search-item.active, .search-item:hover {
    background: #3b3b3b !important;
}
 

 
 .modal-content-md {
    width: 600px;
    background-color: #333;
}
  .modal-header {
background-color: #333;
  	color: #444 !important;
  }
  
  .modal label {
    color: #fff !important;
}
  
  search-container {
  	border-color: #111 !important;
  }
    search-container .items {
    background-color: #333 !important;
    border-top-color: #111 !important; 
    }

.search-item div {
    border-bottom-color: transparent !important;
}

.timesheet-cell-input-comment-text {
    border-color: #111;
    background-color: #444;
    color: #fff;
}

  .modal-content {
  	background-color: #333;
    border: 1px solid #111;
  }

.modal-buttons {
	border-top-color: #111;
}
  .user-name-name {
color: #fff !important;
}
 .popover-header {
    background-color: #333 !important;
}
  .popover-header>span {
    color: #fff !important;
}
  .notifications-count {
    background: #f03 !important;
    border-width: 0 !important;
  }
  .header-logo img {
  	filter: saturate(75);
  }
.timesheet-row-header .timesheet-column-today {
    background-color: rgba(255, 0, 51, 0.3);
}
  @media (min-width: 992px) {
	.page {
    	background: #333;
    	border: 1px solid #222;
	}
}
.search {
	background-color: #333 !important;
}
.extension-manager{
    background-color: #444!important;
}
