css:
/* Scythir's Crappy OTRS FIX */

@-moz-document url-prefix('https://www.your-fancy-domain.com/otrs') {

.MainBox { background-color: gray !important; }

.WidgetSimple > .Header, .WidgetSimple.Collapsed.AnimationRunning > .Header {
    background-color: #333;
    color: #f00;
    text-shadow: 0px 0px 0px #000;
    border-bottom: 0px;
  }
.WidgetSimple > .Header h2, .WidgetSimple.Collapsed.AnimationRunning > .Header h2 {
    color: #aaa;
    text-shadow: 0 1px 0 #000
  }

.WidgetSimple {
    box-shadow: none;
    background: #444;
    color: #ddd;
}

  .WidgetSimple > .Content {
    background-color: #4f4f4f;
    padding: 5px 5px 8px;
    color: #ddd;
  }

.WidgetAction i {
    color: #999;
}
.WidgetAction:hover i {
    color: #ff9922;
}

.WidgetAction.Toggle i {
    color: #777 !important;
    text-shadow: 0px 1px 0px #acacac;
}

  .Tab.Actions li.Selected a {
    color: #999;
  }

/* TICKET TABLE */
.DataTable {
  background-color:#cecdcd;
  color: #000;
    border-color: #888;
}

.DataTable thead th {
background: #262626;
    color: #fff;
border-bottom: 1px solid #888;
border-right: none;
border-top: none;
}

.DataTable thead th.DashboardHeader a, .TableSmall thead th.OverviewHeader a, .DataTable thead th.DashboardHeader span.Gray{
    color: #888;
    padding-left: 10px;
}

.DataTable tbody tr:nth-child(odd) td {
background: #444 !important;
    color: #ccc;
}
.DataTable tbody tr:nth-child(even) td {
background: #222 !important;
    color: #ccc;
}
.DataTable tbody tr:hover td {
    color: #fff;
}
.DataTable tbody tr td a {
    color: #ff9922;
    text-shadow: 0 1px 0 #000;
}
.DataTable tbody tr:hover td a {
    color: #ffc685;
    text-shadow: 0 1px 0 #000;
}


/* TICKET TABLE (SEARCH) */
.TicketList td {
  background-color:#cecdcd;
  color: #000;
    border-color: #888;
}

.TicketList thead th, .ControlRow div {
background: #262626;
color: #fff;
border-bottom: 1px solid #888;
border-right: none;
border-top: none;
}

.TicketList tbody tr:nth-child(odd) td {
background: #444 !important;
    color: #ccc;
}
.TicketList tbody tr:nth-child(even) td {
background: #222 !important;
    color: #ccc;
}
.TicketList tbody tr:hover td {
    color: #fff;
}
.TicketList tbody tr td a {
    color: #ff9922;
    text-shadow: 0 1px 0 #000;
}
.TicketList tbody tr:hover td a {
    color: #ffc685;
    text-shadow: 0 1px 0 #000;
}

.OverviewBox,.OverviewControl > div, body {
    background-color: #444;
}
.ActionRow, .Actions li.Inactive a, span.Pagination a, span.Pagination span.PaginationLimit {
    color: #999;
    background-color: #333;
    text-shadow: 0 1px 0 #000;
    border-bottom: none;
}

.OverviewZoom a {
    background-color: #444;
    color: #aaa;
}


/* TICKET TABLE > BOX ICONS */
/*
.WidgetAction:hover i, .WidgetAction a:focus i {
    color: #797979;
}
.WidgetSimple > .Header, .WidgetSimple.Collapsed.AnimationRunning > .Header > .fa {
    text-shadow: 0px 0px 0px #fff;
}

/* TICKET TABLE > FILTER ICON */

.DataTable thead th.DashboardHeader.FilterActive .ColumnSettingsTrigger, .DataTable thead th.DashboardHeader .ColumnSettingsTrigger:hover, .DataTable thead th.DashboardHeader .ColumnSettingsTrigger.Active, .TableSmall thead th.OverviewHeader.FilterActive .ColumnSettingsTrigger, .TableSmall thead th.OverviewHeader .ColumnSettingsTrigger:hover, .TableSmall thead th.OverviewHeader .ColumnSettingsTrigger.Active {
    box-shadow: none;
}
.DataTable thead th.DashboardHeader.FilterActive .ColumnSettingsTrigger i, .DataTable thead th.DashboardHeader .ColumnSettingsTrigger.Active i, .TableSmall thead th.OverviewHeader.FilterActive .ColumnSettingsTrigger i, .TableSmall thead th.OverviewHeader .ColumnSettingsTrigger.Active i {
    color: #ff9922;
}

/* Stars & sh*t */
.DataTable tbody td.Flags {
    padding: 0 0px;
}
span.UnreadArticles, div.UnreadArticles {
    width: 16px;
    margin-left: 4px;
    padding: 0px 0px !important;
}

.Flag span.PriorityID-3 {
    background-color: 
    #a8a8a8;
}

.Tab.Actions li:first-child a {
    padding-left: 20px;
}
.Tab.Actions li a {
    padding: 5px 5px 3px 2px;
    color: #999;
}
a.AsBlock {
    color: #e86603;
text-shadow: 0 1px 0 #fff
}

#Footer, .Footer {
    background-color: #4f4f4f;
    color: #ddd;
}

/* NAVIGATION */

a.AsPopup.PopupType_TicketAction {
    color: #ddd;
}

.Actions li a, .Actions li span {
    color: #ddd;
}

.Cluster ul ul li a {
background: #555;
}
.Cluster ul ul li a:hover {
    background: #444;
color: #ffc251;
}



/* TICKET VIEW */


.UseArticleColors #ArticleTable a {
    color: #ddd;
}


.Avatar {
    background: #333;
}

h1 {
  color: #fbfbfb;
  font-weight: normal;
  text-shadow: 0 1px 0 #555;
}


.UseArticleColors #ArticleItems .system .ArticleMailContent iframe, .UseArticleColors #ArticleItems .system .ArticleMailContent .ArticleBody, .UseArticleColors #ArticleItems .system .ArticleMailContent .ArticleHTML, .UseArticleColors #ArticleItems .system .ArticleMailContent .ArticleMeta {
    border: 2px solid 
#ffef80;
}

#ArticleItems .Incoming .ArticleMailContentHTMLWrapper, #ArticleItems .Incoming .ArticleHTML, #ArticleItems .Incoming .ArticleBody {
    color: #000;
}

fieldset.TableLike > label, fieldset.TableLike > .Row > label {
text-shadow: 0 1px 0 #222;
}

.UseArticleColors tr.NotVisibleForCustomer.Active > td, .UseArticleColors tr.NotVisibleForCustomer:hover > td {
    background-color: #971f1f !important;
}
.UseArticleColors tr.VisibleForCustomer.Active > td, .UseArticleColors tr.VisibleForCustomer:hover > td {
    background-color: #ffe77f !important;
    color: #000 !important;
}
.UseArticleColors tr.VisibleForCustomer:hover > td a {
    color: #000 !important;
    text-shadow: none;
}

/*!! POPUP WINDOW
*
*/

.LayoutPopup > .Header {
    background-color: #4f4f4f;
}

.LayoutPopup > .Content {
    padding: 20px;
    background-color: #777;
}

div.InputField_Selection div.Text {
    color: #444;
}

form label {
    color: #eee !important;
    font-weight: bold;
    text-shadow: none;
}

form label.Mandatory {
    color: #ffe8b3 !important;
    font-weight: bold;
}

span.ReadyForUpload {
    color: #ddd;
}

.FieldExplanation {
    color: #ddd;
}

/* Input fields */

div.InputField_InputContainer .InputField_Search {
    background-color: #999;
    box-shadow: none;
    border: 1px solid #ccc;
}
fieldset.TableLike > .Field > input {
    background-color: #ccc;
    box-shadow: none;
    border: 1px solid #fff;
    color: #000;
}
div.InputField_Selection div.Text {
    color: #222;
}

.jstree-InputField .jstree-wholerow-ul {
    background: #464646;
    color: #ddd;
}
.jstree-InputField .jstree-wholerow-ul .jstree-wholerow-clicked {
    background-color: #222;
}
.jstree-InputField .jstree-wholerow-ul .jstree-wholerow:hover, .jstree-InputField .jstree-wholerow-ul .jstree-wholerow-hovered, .jstree-InputField .jstree-focused > .jstree-wholerow {
    background: #2d2d2d;
    color: #fff;
}


/* SEARCH */

.Dialog > .Header {
    background: #4a4a4a;
    border-bottom: 0px;
}

.Dialog > .Content > .InnerContent{
    background: #666;
    color: #ddd;
    font-weight: normal;
}

.Dialog > .Content > .ContentFooter {
    background: #4a4a4a;
    border-top: 0px;
}

/* ADMINPANEL */
.ItemListGrid.WithIcons > li > a {
    background: #333;
    text-shadow: 0 1px 0 #111;
    color: #ddd;
}
.ItemListGrid.WithIcons > li > a:hover {
    background: #222;
    color: #ff9922;
}
.ItemListGrid > li span.Description {
    color: #888;
}

/* LOGIN */
.LoginScreen {
    background: #555;
    color: #ddd;
}
#LoginLogo {
    background-color: #333
}
#LostPassword, #BackToLogin {
    color: 
    #ddd;
}
/* MISC */

.LightRow.Bottom {
    background: #444;
    border-bottom: 1px solid #ccc;
}

#Footer, .Footer {
    background-color: #4f4f4f;
    color: #ddd;
}


} /* url-prefix closing bracket */
