css:
@-moz-document url-prefix("https://www.youtube.com/") {

ytd-masthead {
    --paper-input-container-input-align: baseline;
    display: block;
    width: 100%;
    background: var(--yt-spec-brand-background-primary);
}
ytd-app {
    --app-drawer-width: 240px;
    background: #131313;
    display: block;
    left: 0;
    min-height: 100%;
    position: absolute;
    right: 0;
    top: 0;
}
Style Attribute {
    font-size: 10px;
    font-family: Roboto, Arial, sans-serif;
}

#search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 65px;
    border: 0px solid #000000;
    background-color: var(--ytd-searchbox-legacy-button-color);
    border-radius: 0 2px 2px 0;
    margin: 0;
    --yt-icon-button-icon-width: 24px;
    --yt-icon-button-icon-height: 24px;
}

#container.ytd-searchbox {
    position: relative;
    align-items: center;
    background-color: #252525;
    border: 0px solid #565600;
    border-right: none;
    border-radius: 2px 0 0 2px;
    box-shadow: inset 0 1px 2px var(--ytd-searchbox-legacy-border-shadow-color);
    color: var(--yt-searchbox-text-color);
    padding: 2px 6px;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}


:root {
    --yt-spec-general-background-a: #131313;
}

#masthead {
        background-color: #171717;;
}

#guide-content.ytd-app {
    background: #171717;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
}

ytd-thumbnail.ytd-rich-grid-video-renderer {
    width: 100%;
    position: relative;
    box-shadow: 0 0 16px 1px #00000024;
}

ytd-multi-page-menu-renderer {
    display: inline-block;
    background: rgb(25, 25, 25);
    border: 0px solid var(--yt-spec-10-percent-layer);
    border-top: none;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: column;
    -webkit-flex-direction: column;
    flex-direction: column;
}

ytd-active-account-header-renderer {
    background-color: rgba(19, 19, 19, 0.98);
    border-bottom: 0px solid var(--yt-spec-10-percent-layer);
    box-sizing: border-box;
    position: relative;
    padding: 16px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
}

element.style {
    outline: none;
    box-sizing: border-box;
    max-width: 300px;
    max-height: 713px;
    box-shadow: 0 0 16px 9px #00000045;
}
}
