css:
@-moz-document domain("shop.hardware.fr"),
domain("secure.hardware.fr") {
    *,
    body {
        font-family: 'Titillium Web', arial, sans-serif;
        color: #f08219;
    }
    body {
        background-color: #151515;
        overflow-x: hidden;
    }
    .assurance div span.icon {
        background: #111;
        color: #f08219;
    }
    .assurance div {
        background: #222;
    }
    .title-1,
    .title-2,
    .title-3,
    h1,
    h2,
    h3 {
        color: #f08219;
    }
    .configurateur .elements ul .home-carousel li,
    .home-carousel .configurateur .elements ul li,
    .home-carousel .listing ul.list li,
    .home-carousel .listing-avis li,
    .home-carousel .relay-choice .relay-list ul,
    .home-carousel .sbloc,
    .listing ul.list .home-carousel li,
    .listing-avis .home-carousel li,
    .relay-choice .relay-list .home-carousel ul {
        background-color: #0c0c0c;
        box-shadow: 0 2px 6px rgb(0, 0, 0);
    }
    .home-carousel p strong {
        color: #f08219;
    }
    .home-carousel p {
        color: #eee5dc;
    }
    .home-carousel span.pic img {
        border: solid 3px #f08219;
        border-radius: 12px;
        filter: brightness(0.8);
    }
    nav ul#menu li.univers:hover,
    nav ul#menu li.univers > a:hover {
        background-color: #001932!important;
        text-shadow: #f08219 0 0 2px;
    }
    .univers.hover,
    .univers.hover a {
        background-color: #001932!important;
    }
    nav ul#menu li.univers {
        border-right: 1px solid #f08219;
    }
    nav ul#menu li.univers > a {
        color: #f08219;
    }
    nav #logo,
    nav #logoSmall {
        border-right: 1px solid #f08219;
    }
    #moteur {
        border-right: 1px solid #f08219;
    }
    input,
    button,
    select,
    textarea {
        background-color: #000;
    }
    input[type=email],
    input[type=number],
    input[type=password],
    input[type=phone],
    input[type=text],
    textarea {
        border: 1px solid #f08219;
        color: #f08219;
        box-shadow: 0 1px 0 0 #f08219;
    }
    #compte,
    #compte span.text,
    #panier,
    #panier span.text {
        color: #f08219;
    }
    #compte span.icon,
    #panier span.icon {
        color: #f08219;
    }
    header #compte:hover,
    header #panier:hover {
        background-color: #001932!important;
        text-shadow: #f08219 0 0 2px;
    }
    #compte,
    #panier {
        border-right: 1px solid #f08219;
    }
    .header {
        box-shadow: #000 0 0 8px;
    }
    .univers .submenu-container {
        background-color: #001932;
        border-bottom: 5px solid #F08219;
        box-shadow: 0 4px 6px rgb(0, 0, 0, 0.7);
    }
    .univers .volet > li a {
        background-color: #001932;
        color: #f6f6f6;
    }
    .univers .volet .famille {
        color: #f08219;
    }
    .univers .volet > li a.famille:hover {
        color: #f08219;
        text-shadow: #000 0 0 6px;
    }
    .univers .volet ul {
        background-color: #001932;
    }
    nav ul#menu li.univers .volet li.btn-configurateur a strong {
        color: #f08219;
    }
    .univers .volet > li .beforeImg,
    .univers .volet > li .img,
    .univers .volet > li img {
        width: 72px;
        height: 72px;
        border: #f08219 solid 3px;
        border-radius: 8px;
        filter: brightness(0.8);
    }
    .settings .name-it:after {
        background: #f08219;
    }
    .button {
        background: #F08219;
        color: #000;
    }
    .button:active,
    .button:focus,
    .button:visited {
        position: relative;
        color: #000;
    }
    .settings .load-it .connect .button em {
        color: #000;
    }
    .settings .load-it .connect .button em:hover {
        color: #fff;
    }
    .configurateur .elements ul li,
    .listing ul.list li,
    .listing-avis li,
    .relay-choice .relay-list ul,
    .sbloc {
        background: #0c0c0c;
        box-shadow: 0 2px 6px rgb(0, 0, 0);
    }
    .cat-pop a.sbloc img {
        border: solid #f08219 3px;
        border-radius: 12px;
    }
    .button.picto-seul span {
        color: #000;
    }
    .modal-content {
        background-color: #222222b5;
    }
    #modal-products .subtitle-h3 {
        background: #222222b5;
    }
    .dropdown {
        background: #000;
    }
    .dropdown .input {
        border: 1px solid #000;
        box-shadow: 0 1px 0 0 #f08219;
    }
    .dropdown .input.active {
        border-color: #f08219;
    }
    .dropdown .input a,
    .dropdown .input a:visited,
    .dropdown .input a:focus,
    .dropdown .input a:active {
        color: #f08219;
    }
    #modal-products .multiselect .dropdown span.hida {
        font-style: italic;
        color: #f08219;
    }
    .dropdown .input a span.icon {
        color: #f08219;
    }
    .dropdown .list .drop {
        border: 1px solid #000;
        background: #000;
        box-shadow: #000 0 2px 20px;
    }
    .dropdown .list li input:checked ~ label,
    .dropdown .list li input:checked ~ label:hover {
        background: #f08219!important;
        color: #000!important;
    }
    .checkbox label:before {
        background: #f08219;
        border: 1px solid #f08219;
    }
    .checkbox label:after {
        background: #000;
        color: #000;
    }
    .dropdown .list li label:hover {
        background-color: #f08219;
        color: #000;
    }
    .item-list {
        border: 1px solid #f08219;
    }
    .item-list .item-table-head .search-keyword a.reset span {
        color: #df3232;
    }
    table.dataTable head td span.icon-fleche-bas-xl,
    table.dataTable thead th span.icon-fleche-bas-xl {
        color: #f08219;
    }
    table.dataTable thead th {
        border-right: 1px solid #f08219;
    }
    table.dataTable head td,
    table.dataTable thead th {
        color: #f08219;
        background: #111;
    }
    .item-list .item-table-body .item-designation span.product-name {
        color: #f08219;
    }
    .item-list .item-table-body .item-designation a.detail-link span {
        color: #dc3250;
    }
    .item-list .item-table-body .item-designation a.detail-link:hover span {
        color: #dc3250;
    }
    table.dataTable.display tbody tr td {
        border-right: 1px solid #F0821973;
        border-top: 1px solid #f0821973;
    }
    table.dataTable.display tbody tr.even > .sorting_1,
    table.dataTable.display tbody tr.odd,
    table.dataTable.display tbody tr.odd > .sorting_1,
    table.dataTable.order-column.stripe tbody tr.even > .sorting_1,
    table.dataTable.order-column.stripe tbody tr.odd > .sorting_1,
    table.dataTable.stripe tbody tr.odd {
        background: #222;
    }
    table.dataTable tbody tr {
        background-color: #222;
    }
    .configurateur .elements ul li.selected {
        background: #00000000;
    }
    .configurateur .elements .item {
        background: #111;
    }
    a:hover {
        color: #f08219
    }
    .configurateur .elements .item .edit span {
        color: #f08219;
    }
    .configurateur .elements .item .edit:hover span {
        color: #f0821980;
    }
    .configurateur .elements .item .remove span {
        color: #d80707;
    }
    .configurateur .elements .item .remove:hover span {
        color: #d8070780;
    }
    .odd td,
    .even td {
        background-color: #222!important;
    }
    ::-webkit-scrollbar-thumb {
        background: #f08219;
    }
    ::-webkit-scrollbar-corner {
        background: #000;
    }
    ::-webkit-scrollbar {
        width: 8px;
        height: 8px;
        background: #191919!important;
    }
    .cd-products-table .features {
        background: rgba(0, 0, 0, 0.9);
    }
    .cd-products-table .top-info {
        border-color: #001932;
    }
    .cd-products-table .features,
    .cd-products-wrapper {
        border-color: #000000;
    }
    .cd-products-columns .product {
        background: #222;
        border-left: 0px solid #001932;
        border-right: 0px solid #001932;
        border-bottom: 0px solid #001932;
    }
    .cd-products-table .top-info img {
        border: solid #f08219 3px;
        border-radius: 12px;
    }
    .cd-features-list li[data-diff=true] {
        background: #222;
    }
    .cd-features-list li {
        border-color: #001932;
        border: 0px;
    }
    .cd-products-table.top-fixed .features .top-info {
        background: #111;
    }
    .cd-products-table.top-fixed .product .top-info {
        background: #111;
        border-color: #111;
    }
    .prix,
    .prix sup {
        color: #f08219;
    }
    .stock-1,
    .stock-2 {
        background: #111;
    }
    .stock-3,
    .stock-4,
    .stock-5 {
        background: #111;
    }
    .cd-products-table .top-info a.delete span {
        background: #222;
    }
    div.DTS div.dataTables_scrollBody {
        background: #222;
    }
    table.dataTable.display tbody tr:last-child td {
        border-bottom: 1px solid #222;
    }
    aside#description h1 {
        color: #f08219;
    }
    #globalFiches {
        background-color: #222;
    }
    .infosProduit > .checkbox {
        border-radius: 12px;
        background-color: #333;
        padding: 4px;
        box-shadow: #000 0 2px 4px;
    }
    #productphoto {
        background: #111;
        filter: brightness(0.8);
    }
    aside#description h2 {
        color: #c6c6c6;
    }
    section.blocInfos .prix-panier {
        background-color: #222;
    }
    section.blocInfos .infoPrix .modalitePaiement:after {
        border-left: 71px solid #000000;
    }
    section.blocInfos .infoPrix .livraison li p {
        color: #c2c2c2;
    }
    section.blocInfos .infoPrix .eco {
        color: #b2b2b2;
    }
    .warning {
        background: #511d1d;
    }
    .specsTech table td.label {
        color: #c8c8c8;
    }
    .specsTech table td {
        border: 1px solid #3d3d3d;
    }
    .specsTech table td label {
        color: #f08219;
    }
    .specsTech table td.checkbox,
    .specsTech table td.no-checkbox {
        color: #f08219;
    }
    .specsTech table td.checkbox a,
    .specsTech table td.no-checkbox a {
        color: #f08219;
    }
    .specsTech .select-critere span {
        color: #04b5f0;
    }
    .listing-avis li .content {
        color: #b1b1b1;
    }
    .listing-avis li .head {
        border-bottom: 1px solid #f08219;
    }
    .head-list .count-list h1,
    .head-list .count-list p {
        color: #dddddd;
    }
    .filters .title {
        color: #f08219;
    }
    .bloc-filter .checkbox label a,
    .bloc-filter .radio label a {
        color: #e7e7e7;
    }
    .bloc-filter .checkbox label span.nb,
    .bloc-filter .radio label span.nb {
        color: #b7b7b7;
    }
    .bloc-filter {
        border-top: 1px solid #f08219;
    }
    .seo .seo__desc {
        color: #dddddd;
    }
    .listing .description h2,
    .listing .description h2 a {
        color: #f08219;
    }
    .listing .description p {
        color: #cdcdcd;
    }
    .stock-9,
    .stock-6,
    .stock-7 {
        background: #000000;
    }
    .stock-9 em,
    .stock-9 span {
        color: #b12841;
    }
    .button.disabled,
    .button:disabled {
        background: #b12841;
        box-shadow: 0 2px 0 0 #b12841;
    }
    .listing ul.list .checkbox:before {
        background: #222;
    }
    .listing .checkbox label {
        color: #f08219!important;
    }
    .listing ul.list li > a:first-child .pic img {
        filter: brightness(0.8);
    }
    .select-styled {
        border: 1px solid #000000;
        box-shadow: 0 1px 0 0 #000000;
        background: #000;
    }
    ul.select-options {
        border: 1px solid #000000;
        background: #000;
        box-shadow: #000 0 2px 20px;
    }
    .select-styled.active {
        border-color: #000000;
    }
    ul.select-options li:hover {
        background: #f08219;
        color: #000;
    }
    .pagination > .active > a,
    .pagination > .active > a:focus,
    .pagination > .active > a:hover,
    .pagination > .active > span,
    .pagination > .active > span:focus,
    .pagination > .active > span:hover {
        color: #F08219;
        background-color: #000;
    }
    .pagination > li > a:focus,
    .pagination > li > a:hover,
    .pagination > li > span:focus,
    .pagination > li > span:hover {
        color: #000;
        background-color: #f08219;
        border-color: #000;
    }
    .pagination > li.next a span,
    .pagination > li.prev a span {
        color: #000;
    }
    .cart-container .share-container a {
        background: #000;
    }
    .cart-container .share-container a span.txt {
        color: #f08219;
    }
    .cart-choose-country .dsp-table {
        border: 1px solid #f08219;
    }
    .client-account .disconnect span {
        color: #b12841;
    }
    .client-account .disconnect .icon {
        color: #b12841;
    }
    .client-account .tabs span {
        color: #0096c8;
    }
    .client-account .tabs .icon {
        color: #0096c8;
    }
    .client-account .collapser .order,
    .client-account .tabs {
        border-bottom: 1px solid #f08219;
    }
    .client-account .text-content span {
        color: #ddd;
    }
    .client-account .collapse .product-name a,
    .client-account .collapsing .product-name a {
        color: #f08219;
    }
    .client-account .collapse .product-quantity,
    .client-account .collapsing .product-quantity {
        color: #ddd;
    }
    .client-account .text-content {
        color: #fff;
    }
    .client-account .collapse .product-price p,
    .client-account .collapsing .product-price p {
        color: #f08219;
    }
    .client-account .collapse .product-name,
    .client-account .collapsing .product-name {
        color: #1db377;
    }
    .client-account .cancel-command {
        background-color: #000;
    }
    .client-account .collapser .action-collapse .icon {
        color: #f08219;
    }
    .avis .infos {
        background: #111;
    }
    .avis .detail-note li .progress {
        background: #000;
    }
    .conditions {
        padding: 20px;
        background: #111;
    }
    .sbloc-separator > .separator:before {
        background-color: #f08219;
    }
    .avis .average {
        border-right: 1px solid #f08219;
    }
    .dropdown .list ul li a:hover {
        background-color: #222;
    }
    .avis .title strong {
        color: #fff;
    }
    .count-char label {
        display: inline-block;
        color: #ddd;
    }
    input[type=text]:disabled {
        background: #000;
    }
    .client-account .account-settings label {
        color: #f08219;
    }
    .cart-product-list {
        background-color: #111;
    }
    .cart-product-list .thead .dsp-cell {
        border: none;
        text-transform: uppercase;
        color: #fff;
    }
    .cart-product-list p.product-name a {
        color: #f08219;
    }
    .cart-product-list .product-price {
        background-color: #111;
    }
    .cart-product-list .product-price {
        color: #f08219;
    }
    .cart-product-list p.product-name {
        color: #1db377;
    }
    .cart-product-list .dsp-cell p {
        color: #ddd;
    }
    .quantity-button {
        background: #000;
    }
    .cart-product-list .product-img {
        border-top: 1px solid #f08219;
    }
    .cart-product-list .dsp-cell {
        border-top: 1px solid #f08219;
    }
    .cart-product-list.tfoot .dsp-row {
        background-color: #111;
    }
    .cart-product-list.tfoot .cart-promo-code .cart-total {
        background-color: #222;
    }
    .cart-product-list.tfoot .cart-total {
        background-color: #222;
    }
    .cart-product-list.tfoot .cart-total-text {
        color: #f08219;
    }
    .cart-product-list.tfoot .cart-promo-code .cart-total-text {
        color: #fff;
    }
    .current-filters ul li {
        background: #000000;
    }
    .client-account #saved-carts .collapser .collapse,
    .client-account #saved-carts .collapser .collapsing {
        background-color: #111;
    }
    .button.disabled,
    .button:disabled {
        color: #a5a5a5;
    }}
