css:
@-moz-document domain("social.msdn.microsoft.com") {
#BodyBackground,
html,body ,div#ux-footer,.main-content{
    background: #0C0C0C;
    color:#929292;
}
div#ux-footer #Fragment_LeftLinks > .linkList > ul > li > a,div#ux-footer footer.top .linkListTitle{
     color:#c0c0c0;
}
h1, h2, h3, h4, h5, h6, .Forums #categoryFilter h3, .Forums #homepageLeaderboard .box .leaderBoardTitle h4, #content div.box h4, #threadPageSidebarContainer h3,#msdn-technet-en-home .searchBox,#MainContent h1, #MainContent h2,#MainContent label,#MainContent .helper,#MainContent #avatarFile{
    color:#c0c0c0;
}
.body>p>span{
    color:#c0c0c0 !important;
}
li.message div.body blockquote, li.message div.body div.quote,blockquote>div{
    background:#1E1E1E!important;
}
li.message div.body blockquote, li.message div.body div.quote,blockquote>div,.pln{
    color:#cdcdcd;
}

.pun{
    color:#dada65;
}
.kwd{
    color:#7b7bc6;
}
.lit{
    color:#6bc5c5;
}
.typ{
    color:#c369c3;
}
.com{
    color:#c67b7b;
}
#JelloWrapper,.inner,li.message {
    background: #151515 !important; 
}
.sidebar{
    background:#212121;
}
#MainContent .statistic-group{
    background:#303030;
    color:white;
}
#MainContent .statistic-group .statistic-table td{
    color:#cecece;
}
#MainContent .statistic-group .statistic-table tr:first-child .value{
    color:white;
}
#threadActionMenu {
    background: #151515;
}
#MainContent textarea, #MainContent input[type="text"]{
    background:#303030;
    color:white;
    border:none;
}

ul.profile-nav a.nav-link:hover, ul.profile-nav a.nav-link:active, ul.profile-nav a.nav-link.active,ul.rankings-nav li.rank-option:hover a, ul.rankings-nav li.rank-option:active a, ul.rankings-nav li.rank-option.active a,a:link, a:visited{
    border-bottom-color:#5896d1;
    color:#5896d1;
}
.editLink>a:link,.editLink>a:visited{
     color:#000;
}
ul.profile-nav a.nav-link{
    color:#D0D0D0;
}
h1,
#msdn-technet-en-home .forumsTitle span {
    color: #bb3cdd !important;
}
div.replies {
    border-top: solid 2px black;
}
#msdn-technet-en-home .searchBox,#tagSearch{
    border-color:black;
}
#sidebar h3,
#homepageLeaderboard .box .leaderBoardTitle h4 {
    color: white;
}
#quickLinks #quickLinksText span {
    color: white;
}
#relatedThreadsLinkContainer {
    background-color: #0D0D0D;
}
#searchContent .searchbar .searchboxinnercontainer,
#threadsearchbarContainer .searchbar .searchboxinnercontainer,#tagSearch {
    background-color: #21201E;
    color:#636363;
}
.announcementsContainer,#MainContent #msdn-technet-en-home a.seeAnnouncementsLink{
    background-color:#303030;
    color: white !important;
}
#msdn-technet-en-thread #threadPageContainer li.message div.menu.thread,
#msdn-technet-en-thread #threadPageContainer li.message div.menu.message,
div.expandedThreadContainer li.message div.menu.thread,
div.expandedThreadContainer li.message div.menu.message {
    background: #151515;
}
.unified-baseball-card-mini .profile-mini .profile-mini-points {
    color: white;
}
.unified-baseball-card-mini .profile-mini {
    color: #D5D5D5;
}
div#msdn-technet-en-thread div#threadPageContentContainer,#allThreadsContainer {
    border-left: 3px solid #262626;
}
div.answers > h3,
div.replies > h3 {
    border-top: solid 2px #262626;
}
div#msdn-technet-en-thread #threadPageContainer .messageContent {
    border-bottom: solid 2px #262626 !important;
}
.footerLinksContainer {
    border-top: solid 2px #262626;
}
.footerSubLinksContainer {
    border-top: 1px #262626 solid;
}
div#ux-footer footer.bottom span.logoSpan img, .microsoftlogo{
    filter: invert(1);
}

#userCard div.unified-baseball-card,
.unified-baseball-card-compact,
.unified-baseball-card-hover div.unified-baseball-card {
    background: black;
}
.unified-baseball-card .baseball-card-header .baseball-card-summary .baseball-card-point-count,
.unified-baseball-card .baseball-card-header .baseball-card-summary .baseball-card-points-label,
.unified-baseball-card .baseball-card-header .baseball-card-summary .baseball-card-percentile {
    color: white !important;
}

.unified-baseball-card .baseball-card-header .baseball-card-summary,
.unified-baseball-card .baseball-card-header .crop,
.unified-baseball-card-compact-hover .baseball-card-compact-header .baseball-card-summary, .unified-baseball-card-compact .baseball-card-compact-header .baseball-card-summary{
    background-color: #ffffff3d;
    border-bottom-color: #1d1d1d;
}
div.unified-baseball-card,
.unified-baseball-card-compact,
.unified-baseball-card-hover div.unified-baseball-card,
.unified-baseball-card .baseball-card-recent-activity,
.unified-baseball-card .baseball-card-joined,
.unified-baseball-card-compact-hover .baseball-card-recent-activity,
.unified-baseball-card-compact-hover .baseball-card-joined,
.unified-baseball-card-compact .baseball-card-recent-activity,
.unified-baseball-card-compact .baseball-card-joined,
.unified-baseball-card .baseball-card-medals .baseball-card-gold-img p,
.unified-baseball-card .baseball-card-medals .baseball-card-bronze-img p,
.unified-baseball-card .baseball-card-medals .baseball-card-silver-img p,
.unified-baseball-card-compact-hover .baseball-card-medals .baseball-card-gold-img p,
.unified-baseball-card-compact-hover .baseball-card-medals .baseball-card-bronze-img p,
.unified-baseball-card-compact-hover .baseball-card-medals .baseball-card-silver-img p,
.unified-baseball-card-compact .baseball-card-medals .baseball-card-gold-img p,
.unified-baseball-card-compact .baseball-card-medals .baseball-card-bronze-img p,
.unified-baseball-card-compact .baseball-card-medals .baseball-card-silver-img p,
.unified-baseball-card .baseball-card-display-name, 
.unified-baseball-card-compact-hover .baseball-card-display-name, 
.unified-baseball-card-compact .baseball-card-display-name,
.unified-baseball-card-compact-hover .baseball-card-compact-header
.baseball-card-summary,
.unified-baseball-card-compact .baseball-card-compact-header .baseball-card-summary,
.unified-baseball-card-compact-hover .baseball-card-compact-header .baseball-card-summary .baseball-card-point-count, 
.unified-baseball-card-compact .baseball-card-compact-header .baseball-card-summary .baseball-card-point-count,
.unified-baseball-card-compact-hover .baseball-card-compact-header .baseball-card-summary .baseball-card-points-label, .unified-baseball-card-compact .baseball-card-compact-header .baseball-card-summary .baseball-card-points-label
{
    color: #999 !important;
}
.unified-baseball-card a.baseball-card-display-name:hover, .unified-baseball-card-compact-hover a.baseball-card-display-name:hover, .unified-baseball-card-compact a.baseball-card-display-name:hover{
    color:white !important;
}
}
