css:
@-moz-document domain("ggrecon.com") {
.javascriptdisabled, .social-link-contain, .wrapper, .related-items-contain, .calendar-items-contain, footer, .tweet-block, iframe, .author-social
{display: none}
*
{-webkit-font-smoothing: auto !important;}
*
{font-family: "Verdana Pro",Verdana !important}
p, .text-block h1, .text-block h2, .text-block h3, .text-block h4, .text-block h5, .text-block h6, .left-menu-contain .links-contain
{color: white}
html, body, .right-page-container, .left-content
{background: black !important}
html body .body-content-container nav, .left-menu-contain, .news-page-contain .grid-container .grid .grid-item, .contributors-view-page-contain .grid-container .grid.grid-default .grid-item.contributor-info-block
{background: hsl(240, 50%, 10%)}
.article-view-container .article-content-contain .left-content .left-content-inner .page-title, .article-view-container .article-content-contain .left-content .left-content-inner .author-container, .text-block a
{color: hsl(240, 50%, 75%)}
.text-block ul
{color: hsl(0, 0%, 75%)}
.text-block strong
{font-weight: 900;}
.text-block strong
{font-stretch: condensed;}
*
{line-height: 1.5 !important}
.text-block h1
{font-size: 28px}
.text-block h2
{font-size: 24px}
.text-block h3
{font-size: 20px}
p
{font-size: 16px}
}
