/*global styles*/

body {
    background-color: #222222;
    color: #ececec;
    
}

::selection {
background: #353940; /* Safari */
}
::-moz-selection {
background: #353940; /* Firefox */
}

a {
    color: #ececec;
}

a:hover {
    color: #d7d7d7;
}

dfn, cite, i {
    color: #ccc;
}

span {
    background-color: #d7d3d3;
    border-radius: 3px;
}

blockquote.aligncenter, .wp-caption.aligncenter, img.aligncenter, img {
    filter: invert(84%);
}

/*left bar*/

.sidebar, body:before {
    background-color: #191919;
    color: #ececec;
}

.main-navigation ul {
    color: #ececec;
}

.custom-logo {
    filter: invert(0%);
    content: url(data:image/png;base64,);
    
}

/*right content*/

.hentry {
/*    background-color: #373b3e;*/
    background-color: #292929;
    color: #ececec;
    border-radius: 5px;
}

#menu,#before-themes {
    border: 1px solid #222222;
    background-color: #222222;
    border-radius: 5px;
}

a.show_hide:visited {
    color: #d0cfcf;
    outline: 0;
}

a.show_hide {
    color: #d0cfcf;
    outline: 0;
}

.post-navigation {
    background-color: #292929;
    border-radius: 5px;
}

.post-navigation .meta-nav {
    color: rgba(255, 255, 255, .7);
    background-color: #292929;
}

.post-title {
    color: #ececec;
    background-color: #292929;
}

span.b-test-work-description_strong {
    background-color: #292929;
}




/*Comments*/

.comments-area {
    background-color: #292929;
    border-radius: 5px;
}

.comment-author,.comment-metadata a, .pingback .edit-link a {
    
    color: rgba(255, 255, 255, .7);
    
}

.comment-list .reply a {
    color: #ececec;
    border: 1px solid rgba(70, 70, 70, .1);
}

.comment-list .reply a:hover {
    color: #d2d2d2;
    border: 1px solid rgba(219, 219, 219, .1);
}

.comment-notes, .comment-awaiting-moderation, .logged-in-as, .form-allowed-tags {
    color: rgba(255, 255, 255, .7);
}

input, textarea {
    background-image: -webkit-linear-gradient(#222, #222);
    border-radius: 5px;
}


/*footer*/

.site-info, .site-info a {
   color: #ececec; 
}

.site-footer {
    background-color: #292929;
    border-radius: 5px 5px 0px 0px;
}

/*scroll*/



