css:
@-moz-document domain("edgenuity.com") {
.mainhead {
    background: linear-gradient(-90deg, #00ccff, #33cc33);
}
#wrap  {
background-image: url('https://i.imgur.com/s8I7T0U.jpg');
    background-position: left top;
    background-size: 1920px 1080px;
    height: 900px;
    background-repeat: no-repeat;
position: relative;
}
BODY {
background-color: #00000000;
bottom: 5px;
overflow: hidden;
}
.Toolbar .FramesList {
top: 115px;
margin: 61px;
}
em#frameProgress {
top: 250px;
color: #FFFFFF;
font-weight: bold;
text-decoration: underline;
}

HTML {
background-color: #00000000;
}
#lesson-title {
    font-size: 24px;
    color: #6ce322;
    line-height: 26px;
    margin: 0px 0px 6px 0px;
font-weight: bold;
}
body#footer {
background-image: url('https://i.postimg.cc/sXTqnx6p/art.jpg');
}
.Toolbar .FramesList li.FrameComplete a {
border: 1px solid #ffffff;
}
.Toolbar .FramesList li.FrameCurrent a {
border: 1px solid #ffffff;
    background-image: linear-gradient(to bottom, #38ce19, #1c92a5)
}
.Toolbar .FramesList li.FrameRight a {
    -webkit-border-radius: 160px;
}
.Toolbar .FramesList li a {
border: 1px solid #FFFFFF;
}
.Toolbar .FramesList li.FrameLeft a {
    -webkit-border-radius: 160px;
}
}
