css:
@-moz-document domain('docs.nestjs.com') {
  header .menu-wrapper li:last-of-type a {
    background: #262626 !important;
    color: #e0234e !important;
}
app-menu {
    background: #151515 !important;
}
app-menu app-menu-item h3 {
    color: #f44336 !important;
}
app-menu app-menu-item a.active h3 {
    color: #0894e2 !important;
}
app-menu .sub-nav a {
    color: #0894e2 !important;
    font-weight: bold !important;
}
app-menu .sub-nav a.active {
    color: #ed2945 !important;
}
app-menu app-menu-item .nav-item .arrow-icon {
    fill: #f44336 !important;
}
app-menu app-menu-item .nav-item.opened .arrow-icon {
    fill: #0594e2 !important;
}
.container {
    background: #000;
}
.page-wrapper {
    color: #adaaaa;
}
.page-wrapper h1, .page-wrapper h2, .page-wrapper h3, .page-wrapper h4, .page-wrapper h5, .page-wrapper h6 {
    color: #0894e2;
}
strong {
    color: #e0e0e0;
}
.toc-wrapper .ng-star-inserted a {
    color: #ffffff !important;
}
.toc-wrapper .ng-star-inserted.current a {
    color: #0894e2 !important;
    font-weight: bold !important;
}
.toc-wrapper .ng-star-inserted.current::before {
    background: #0894e2 !important;
    border-color: #0894e2 !important;
}
.github-links svg {
    color: #f44336 !important;
}
.github-links svg:hover {
    color: #0894e2 !important;
}
.file-tree {
    background: #151515;
    border: 4px solid #151515;
}
.file-tree .item {
    color: #fff;
}
tr:nth-of-type(even) td {
    background: #0c0c0c;
}
code {
    background: #0b77b3;
    text-shadow: 0 0 10px black;
    color: #fff;
}
blockquote {
    background: #151515 !important;
}
}
