css:
@-moz-document domain("members.popcash.net") {
body {
    font-family: "Roboto", Helvetica Neue, Helvetica, Arial, sans-serif;
    font-size: 13px;
    line-height: 1.5384616;
    color: #dedede;
    background-color: #171717;
}

.navigation {
    background-color: #212124;
}

.sidebar-default .navigation li > a {
    color: #dedede;
}

.sidebar-default {
    color: #dedede;
}

.panel {
    border-color: #3a3a3a;
    background-color: #212124;
    color: inherit;
}

.category-content {
    background-color: #212124;
}

.alpha-teal {
    background-color: #212124;
    border-color: #000;
}

.panel-default > .panel-heading {
    color: #dedede;
    background-color: #212124;
    border-color: #ddd;
}

.panel-flat > .panel-heading {
    background-color: #212124;
}

.sidebar-default .navigation li > a:hover, .sidebar-default .navigation li > a:focus {
    background-color: #42444d;
}

.sidebar-default .navigation > li.active > a, .sidebar-default .navigation > li.active > a:hover, .sidebar-default .navigation > li.active > a:focus {
    background-color: #444;
    color: #dedede;
}

.navbar-default {
    background-color: #171717;
}

.navbar-default.navbar-fixed-bottom {
    border-top-color: #3c3c3c;
    border-bottom-color: #3c3c3c;
}

.sidebar-default .sidebar-content {
    border-color: #464646;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #212124;
}

.table-hover > tbody > tr:hover {
    background:  #2f2f33 !important;
}

.grid table.data-viewer {
    border: 1px solid #393939;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 1px solid #313131;
}

.grid table.data-viewer>thead th {
    border-left: 1px solid #464646;
}

.form-control {
    color: #dedede;
    background-color: #262629;
    border: 1px solid #464646;
}

.dropdown-menu {
    background-color: #212124;
    border: 1px solid #3e3e3e;
}

.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
    color: #dedede;
    background-color: #333;
}

.dropdown-menu > li > a {
    color: #d2d2d2;
}

.sidebar-default .navigation li.active > a, .sidebar-default .navigation li.active > a:hover, .sidebar-default .navigation li.active > a:focus {
    background-color: #383838;
    color: #dedede;
}

.dropdown-menu .divider {
    background-color: #424242;
}

.navbar-inverse {
    background-color: #2f2f2f;
    border-color: #37474F;
}

.bootstrap-select > .btn.btn-default, .bootstrap-select > .btn.btn-default.disabled {
    background-color: #262629;
    border-color: #464646;
}

td {
  background-color:#212124;
}

.table > thead > tr > th {
    border-bottom: 1px solid #464646;
}

.panel-default > .panel-heading {
    color: #dedede;
    background-color: #212124;
    border-color: #3a3a3a;
}

hr {
    border-top: 1px solid #3a3a3a;
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    color: #dedede;
    background-color: #2b2b2f;
    border: 1px solid #171717;
    border-bottom-color: transparent;
}

.nav-tabs-left > .nav-tabs-highlight > li.active > a, .nav-tabs-left > .nav-tabs-highlight > li.active > a:hover, .nav-tabs-left > .nav-tabs-highlight > li.active > a:focus {
    border-top-color: #212124;
    border-left-color: #EC407A;
}

.nav-tabs-left > .nav-tabs > li.active > a, .nav-tabs-left > .nav-tabs > li.active > a:hover, .nav-tabs-left > .nav-tabs > li.active > a:focus {
    border-bottom-color: #2b2b2f;
    border-right-color: transparent;
}

.nav-tabs-left > .nav-tabs {
    border-right: 1px solid #2b2b2f;
    border-right-width: 1px;
    border-right-style: solid;
    border-right-color: rgb(43, 43, 47);
}

.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #262629;
    opacity: 1;
}

.nav-tabs > li > a:hover, .nav-tabs > li > a:focus {
    background-color: transparent;
    border-color: transparent;
    color: #dedede;
}

.contact-sidebar {
    background: #171717;
    border: 1px solid #464646;
}
}
