css:
/* i really want this to be global */

#jenkins, #jenkins table, #jenkins form, #jenkins td, #jenkins th, #jenkins p {
    color: white !important;
}

table.parameters > tbody:hover {
    background-color: #000000 !important;
}

#breadcrumbs LI A:link, #breadcrumbs LI A:visited{
    color: white !important;
}

.header a.sortheader {
    color: black !important;
}

table.stripped tr:hover, table.stripped-even tr:hover, table.stripped-odd tr:hover {
    background: #00000070 !important;
}

.tabBar [type=radio]:checked ~ a {
    background: inherit !important;
}

.tabBar .tab a:hover {
    background: #0000006b !important;
}

#jenkins{
    background-color: #000000c2 !important;
}

table.stripped-odd tr:nth-child(odd) {
    background-color: #303030 !important;
}

#pipeline-box .table-box .table-viewPort tr.totals .stage-start .cell-color{
    color: white !important;
}

#pipeline-box .table-box .table-viewPort .stage-start .cell-box {
    background-color: #00000047 !important;
}

#pipeline-box .table-box tr:nth-of-type(4n+3) .stage-start .cell-box, #pipeline-box .table-box tr.in-progress-run .stage-start .cell-box {
    background: rgba(0, 0, 0, 0.46) !important;
}

#pipeline-box .table-box .table-viewPort .stage-start .cell-box .stage-end-icons .link{
    color: white !important;
}

#jenkins .yuimenu .bd {
    background-color: #303030 !important;
    border: none !important;
    box-shadow: none !important;   
}

#breadcrumbs LI:hover, A.breadcrumbBarAnchor.mouseIsOverMenuSelector {
    background-color: #0000005c !important;
}

.cbwf-widget .alert-info {
    background-color: rgb(52, 52, 52) !important;
    border-color: rgba(255, 255, 255, 0.56) !important;
    color: rgb(255, 255, 255) !important;
}

.cbwf-popover .alert {
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.25) !important;
    text-shadow: none !important;
    border: none !important;
}

.jobsTable .jobName .badge a {
    color: black !important;
    text-shadow: none !important;
}

footer, #breadcrumbBar, #footer-container, .top-sticker-inner{
    background-color: #4c4c4c !important;
}

.pane-header, .pane-footer{
    background-color: transparent !important;
    color: white !important;
}

#pipeline-box .table-box tr td, .changeset-box.no-changes, .jobsTable .stage-cell.FAILED .stage-wrapper{
    text-shadow: none !important;
}

.jobsTable .stage-wrapper:hover {
    box-shadow: inset 0px -10px 100px -20px #0000007a, inset #d7d7d7 0 0 0 1px !important;
}

#pipeline-box .table-box .table-viewPort .stage-start .cell-box .stage-start-time{
    background-color: #0000004f !important;
    border-color: black !important;
}

.jobsTable .stage-start-time .date {
    background-color: #00000033 !important;
}

table.stripped tr:nth-child(even) {
    background: #00000036 !important;
}

table.stripped tr:nth-child(odd) {
    background-color: #0000006b !important;
}

.pane.build-details a, .pane.build-details a:visited {
    color: white !important;
}

#jenkins a:visited, #jenkins a:link {
    color: #ffffff !important;
}

table.sortable a.sortheader {
    color: black !important;
}
