css:
@-moz-document domain("ay.gy"), domain("adf.ly") {
body {
    background: #0e0e0e;
}

body, button, input, select, textarea {
    font-family: sans-serif;
    color: #dedede;
}

.data nav.menu {
    background: #222225;
}

section.page > .container .highlight {
    background:#222225;
    border-bottom: 1px solid #404040;
}

section.page > .container .highlight ul.earnings h4 {
    color: #dedede;
    text-shadow: 0 1px 0 #fff0, 0 -1px 0 #283a4c;
}

section.page > .container {
    background: #222;
}

#footer {
    background: #131313;
}

a:link, a:visited, a:active {
    color: #dedede;
}

.data nav.menu li {
    float: left;
    clear: both;
    width: 100%;
    font-size: 14px;
    font-weight: bold;
    color: #3478b5;
    text-shadow: 0 0px 0 #fff;
    height: 35px;
    line-height: 35px;
    text-indent: 46px;
    cursor: pointer;
}

.data nav.menu li.active {
    color: #3f5971;
    background: #2b2b2b;
    background-image: initial;
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: rgb(43, 43, 43);
    width: 235px;
    height: 36px;
    margin-top: -1px;
    margin-left: -2px;
    text-indent: 48px;
}

section.page > .container .highlight ul.earnings.totals {
    background: #244e16 repeat-y top right;
    border: 1px solid #39bf34;
    border-radius: 10px;
}

section.page > .container .highlight ul.earnings.totals li h4 {
    color: #dedede;
    text-shadow: 0 1px 0 white, 0 -1px 0 #0e4573;
}

#monthly-earnings-summary a {
    color: #dedede;
    text-shadow: 0 1px 0 #000000;
}

.page table thead th {
    background-image: linear-gradient(#222222, #222222);
}

section.dashboard section.links nav.menu .find {
    background: #222222;
}

section.dashboard section.links nav.menu .find input {
    background-color: #2b2b2b;
}

.page h3 {
    color: #dedede;
}

.page table tbody td.total, #links-tabs table tfoot th.total {
    color: #dedede;
    text-shadow: 0 0px 0 #000;
    background: #2b2b2b;
}

.page table tbody td.sum, #links-tabs table tfoot th.sum {
    font-size: 15px;
    font-weight: bold;
    color: #dedede;
    background: #2b2b2b;
}

.page table tbody {
    color: #dedede;
}

section.dashboard .highlight nav.sort ul.time > li a {
    float: left;
    position: relative;
    height: 26px;
    line-height: 26px;
    text-align: center;
    color: #dedede;
    padding: 0 9px;
    text-shadow: 0 0px 0 #000000;
    border-top: 1px solid #000000;
    border-left: 1px solid #000000;
    border-right: 1px solid #000000;
    border-bottom: 1px solid #000000;
    background: #000000;
    background-image: linear-gradient(#000000, #000000);
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: rgb(0, 0, 0);
    background-image: linear-gradient(#000000, #000000);

background: #222222;
    background-image: initial;
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat-x: initial;
    background-repeat-y: initial;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: rgb(34, 34, 34);
}

section.dashboard section.links table td.actions, section.dashboard section#groupsSection #groupsTable td.actions {
    padding: 0;
    width: 175px;
    width: 190px\9;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #ffffff), color-stop(100%, #f5f5f5));
    background-image: -webkit-linear-gradient(#ffffff, #f5f5f5);
    background-image: -moz-linear-gradient(#ffffff, #f5f5f5);
    background-image: -o-linear-gradient(#ffffff, #f5f5f5);
    background-image: -ms-linear-gradient(#ffffff, #f5f5f5);
    background-image: linear-gradient(#2b2b2b, #2b2b2b);
}

.page h4 {
    color: #7b92a4;
    text-shadow: 0 0px 0 #000;
}

section.dashboard section.links table h4 a {
    color: #dedede;
}

.dataTables_paginate {
    color: #dedede;
    background-image: linear-gradient(#222225, #222225);
}

.multiplebgs #header nav {
    background: bottom left no-repeat, bottom right no-repeat;
    background-color: #2b2b2b;
}

#header nav li a {
    display: block;
    padding: 0 20px;
    padding-top: 54px;
    text-align: center;
    color: #dedede;
    line-height: 1em;
    font-size: 15px;
    text-shadow: 0 0px 0 #000000;
}

#top_bar {
    background-color: #2b2b2b;
}

section.page > .container section.bar {
    width: 899px;
    height: 113px;
    position: relative;
    z-index: 5;
    top: -3px;
    margin: 0 auto;
    background: #2b2b2b;
    border: 1px solid #000000;
    border-top: none;
    -moz-box-shadow: #cccccc 0 8px 6px;
    -webkit-box-shadow: #cccccc 0 8px 6px;
    -o-box-shadow: #cccccc 0 8px 6px;
    box-shadow: #cccccc 0 8px 6px;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #fffdf2), color-stop(100%, #000000));
    background-image: -webkit-linear-gradient(#fffdf2, #000000);
    background-image: -moz-linear-gradient(#fffdf2, #000);
    background-image: -o-linear-gradient(#fffdf2, #000);
    background-image: -ms-linear-gradient(#fffdf2, #000);
    background-image: linear-gradient(#2b2b2b, #2b2b2b);
    -moz-transition-duration: 0.8s;
    -webkit-transition-duration: 0.8s;
    -o-transition-duration: 0.8s;
    transition-duration: 0.8s;
}

section.page > .container section.bar section.profile {
    float: left;
    clear: left;
    width: 268px;
    height: 93px;
    position: relative;
    padding-top: 20px;
    border-right: 1px solid #e4db9e;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #fffdf2), color-stop(100%, #fff7bf));
    background-image: -webkit-linear-gradient(#fffdf2, #000);
    background-image: -moz-linear-gradient(#fffdf2, #000);
    background-image: -o-linear-gradient(#fffdf2, #000);
    background-image: -ms-linear-gradient(#fffdf2, #000);
    background-image: linear-gradient(#2b2b2b, #2b2b2b);
    -moz-transition-duration: 0.8s;
    -webkit-transition-duration: 0.8s;
    -o-transition-duration: 0.8s;
    transition-duration: 0.8s;
}

section.page > .container section.bar section.profile p {
    margin: 0;
    font-size: 13px;
    font-weight: bold;
    color: #dedede;
}

section.page > .container section.bar section.profile h3 {
    color: #2b81ce;
}

section.page > .container section.bar .shadow {
    display: block;
    width: 899px;
    height: 4px;
    background: #2b2b2b;
    position: absolute;
    top: 114px;
    z-index: 5;
    -moz-transition-duration: 0.8s;
    -webkit-transition-duration: 0.8s;
    -o-transition-duration: 0.8s;
    transition-duration: 0.8s;
}

section.page > .container section.bar section.profile {
    float: left;
    clear: left;
    width: 268px;
    height: 93px;
    position: relative;
    padding-top: 20px;
    border-right: 1px solid #4a4a4a;
    border-right-width: 1px;
    border-right-style: solid;
    border-right-color: rgb(74, 74, 74);
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #fffdf2), color-stop(100%, #383838));
    background-image: -webkit-linear-gradient(#fffdf2, #000);
    background-image: -moz-linear-gradient(#fffdf2, #000);
    background-image: -o-linear-gradient(#fffdf2, #000);
    background-image: -ms-linear-gradient(#fffdf2, #000);
    background-image: linear-gradient(#2b2b2b, #2b2b2b);
    -moz-transition-duration: 0.8s;
    -webkit-transition-duration: 0.8s;
    -o-transition-duration: 0.8s;
    transition-duration: 0.8s;
}

#footer .background {
    background: bottom center no-repeat;
}

section.dashboard section.links nav.menu .find h5 {
    font-size: 14px;
    font-weight: bold;
    color: #dedede;
    padding-bottom: 8px;
    line-height: 1em;
}

section.page > .container .highlight ul.earnings {
    float: left;
    color: #d2d2d2;
    margin: 0;
    padding: 6px 0 20px 0;
    width: 280px;
    text-align: center;
}

.button {
    color: #000;
    background-image: linear-gradient(#bfbfbf, #8c8c8c);
}

.button.current, .button:active {
    background-image: linear-gradient(#bfbfbf, #8c8c8c);
}

#cookie_notice {
    color: #757575;
    background-color: #17171a;
}

#cookie_notice a {
    color: #757575;
    text-decoration: none;
}

#login {
    background: #212121;
}

#login h2 {
    color: #dedede;
}

form input {
    color: #black;
    border: 1px solid #bcbcbc;
    background-image: linear-gradient(#a0a0a0, #e6e6e6);
}

form label {
    color: #aaa;
}

body.login #container {
    background: none;
}

#alert header, .validationAlert header, #container.secondary #main .container > section .validationAlert header {
    background-image: linear-gradient(#212121, #212121);
}

#alert, .validationAlert {
    color: #dedede;
}

#alert, .validationAlert {
    background-image: linear-gradient(#512020, #863232);
}

.home #main {
    background: #1f1f1f;
}

.home #main section.features article p {
    color: #dedede;
}

.home #main section.features article h1 {
    font-size: 17px;
    font-weight: bold;
    color: #dedede;
    padding-bottom: 12px;
    text-shadow: 0 0px 0 #000;
}

.home #main .sidebar .quote article {
    background: #252525;
    border: 1px solid #4c4c4c;
}

.home #header {
    background: #2a2a2e;
}

.home #header .container .gettingstarted header {
    border-bottom: 1px solid #353535;
    background: #333333;
}

.home #header .container .gettingstarted {
    background: #262626;
}

.home #header .container .gettingstarted header h1 {
    color: #dedede;
    text-shadow: 0 0 1px #404040;
}

.home #header .container .gettingstarted article.register h3 a {
    color: #dedede;
}

.home #header .container .gettingstarted article h3 {
    color: #dedede;
}

.home #header .container .gettingstarted article.paid {
    border-right: 1px dotted #505050;
    border-left: 1px dotted #505050;
}

.lightBlue {
    background: #191919;
    background-image: -webkit-gradient( linear, left bottom, left 2, color-stop(0.03, #424242), color-stop(0.90, #424242) );
}

.home #header .container form#shrink input {
    background: #c1c1c1;
}

.home footer#footer, .secondary footer#footer {
    background: #1f1f1f;
}

.home #header .container .join p {
    color: #dedede;
}

.ui-widget-content {
    border: 1px solid #5e5e5e;
    background: #292929 50% top repeat-x;
    color: #dedede;
}

div.container section div.common {
    background: #2b2b2b;
}

#container.secondary #main .container > section header {
    border-bottom: 1px solid #656565;
    background: #333333;
    background-color: rgb(51, 51, 51);
}

#container.secondary .wrapper {
    background: #1f1f1f;
}

section.tools .data .panels {
    background: #222225;
    border-left: 1px solid #464646;
}

#tools-tabs .panel .code {
    background-color: #2c2c2f;
}

#tools-tabs .panel .wrap {
    background: #2c2c2f;
}

textarea {
    background: #2c2c2f;
}

#tools-tabs .panel h3 {
    color: #dedede;
}

#tools-tabs .panel .wrap label {
    color: #2f75b3;
}

#tools-tabs {
    background: #222225;
    background-color: rgb(34, 34, 37);
    border: 1px solid #393939;
    border-top-color: rgb(47, 47, 47);
    border-right-color: rgb(43, 43, 43);
    border-bottom-color: rgb(60, 60, 60);
    border-left-color: rgb(74, 74, 74);
}

.page h2 {
    color: #3a5872;
    text-shadow: 0 0px 0 #000;
}

#tools-tabs .panel .wrap textarea {
    border: 1px solid #4c4c4c;
}

#tools-tabs .panel h2 {
    color: #236299;
}
}
