.avatar__default {
    background-image: url(https://preview.ibb.co/k8T0H7/sprite_192dpi.png)!important;
}

.profile__header>.avatar {
    position: absolute!important;
    left: 25px!important;
    top: 50px!important;
    border: 3px solid rgba(0, 0, 0, 0)!important;
    box-sizing: content-box!important;
    background-color: rgba(0, 0, 0, 0)!important;
}

.profile__section {
    border-top: 1px solid rgba(233, 233, 233, 0.0)!important;
    margin: 0 20px!important;
}

