    background-color: rgba(105, 105, 105, 0.80)!important;
    border: 1px solid rgba(233, 233, 233, 0.15)!important;
}

.select select {
    background-color: rgba(105, 105, 105, 0.80)!important;
}

.select__icon {
    background-color: rgba(255, 255, 255, 0)!important;
    color: #ffffff!important;
}

.input_focus, .input:focus {
    border-color: #323232!important;
}

