css:
@-moz-document domain("trimps.github.io") {
/* Commonly toggled */
/* U2 */
#Trap,#Barn,#Shed,#Forge,#Hut,#House,#Mansion,#Hotel,#Resort,#Wormhole,#Gateway,
#Farmer,#Lumberjack,#Miner,#Scientist,#Explorer {
    display: /*[[U2Display]]*/;
}

/* Building queue */
#queueContainer {
    display: /*[[QueueDisplay]]*/;
}
}

@-moz-document domain("trimps.github.io") {
#allA {
    border-bottom-width: 0 !important;
    line-height: 4px !important;
}

#battleSideTitle {
    font-size: 0.8em !important;
}

#buildingsTab,
#jobsTab,
#upgradesTab,
#equipmentTab {
    display: none;
}

#buyContainer {
    height: 263px !important;
}

#buyHere {
    display: flex !important;
    flex-direction: column !important;
    margin: 1px 0 !important;
    padding: 0 5px !important;
}

#generatorProducingContainer {
    font-size: 9px !important;
}

#generatorTickContainer {
    transform: scale(.9, .9) !important;
}

#generatorTitle {
    font-size: 9px !important;
}

#jobsContainer {
    order: 3;
}

#numTabs {}

#outerBuyContainer {
    height: 328px !important;
}

#selectedHeirloom > * {
    font-size: 1.3vw;
}

#upgradesContainer {
    order: 4;
}

.titleDiv {
    font-size: 11px !important;
    margin: 0 !important;
    padding: 0 !important;
}

a {
    font-size: 11px !important;
}

div.natureTop {
    font-size: 18px !important;
}

span.natureBuyIcon {
    border-width: 2px !important;
    margin-bottom: 1px !important;
}
}
