css:
@-moz-document domain("app.clickdeploy.io") {
/*Main color variables*/
:root
{
	/*User colors*/
	--main-background: #222222;
	--second-background: #323232;
	--hover-background: #444;
	--main-text: #bbb;
	--dimmer-text: #bbb;
    --button-background: #333;
}

.nice-grey, h4, label {
    color: var(--main-text) !important;
}

body, table {
    color: var(--main-text) !important;
    background: var(--main-backgrond) !important;
    background-color: var(--main-background) !important;
}

tr:hover{
    background-color: var(--hover-background) !important;
}

.table-header[_ngcontent-c8] a[_ngcontent-c8] {
    color: var(--main-text) !important;
}

.panel {
    background: var(--main-background) !important;
    border-color: var(--second-background) !important;
}

.panel-heading, .form-inline {
    background: var(--main-background) !important;
    border-color: var(--second-background) !important;
}

button,input,select,textarea {
    background-color: var(--button-background) !important;
    color: var(--dimmer-text) !important;
}

.panel-heading h3{
        color: var(--main-text) !important;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover{
    background-color: var(--second-background) !important;
}

.nav-tabs>li>a:hover{
    background-color: var(--hover-background) !important;
}

.form-segment {
    background: var(--second-background) !important;
}

element.style {
    background-color: var(--hover-background) !important;
}

.btn-primary {
    color: #fff !important;
    background-color: #337ab7 !important;
    border-color: #2e6da4 !important;
}

.modal-content{
    color: var(--main-text) !important;
    background: var(--main-backgrond) !important;
    background-color: var(--main-background) !important;
    border-color: var(--second-background) !important;
}

orgwithiconcmpnt div{
    background-color: var(--hover-background) !important;
    border: 1px dashed var(--hover-background) !important;
}

selectednodelistcmpnt > div {
    border-top: 6px solid var(--dimmer-text) !important; 
}

.progress-bar-warning{
    background-color: #337ab7 !important;
}

deployresultcmpnt > div > div > div > div > div {
    color: #222 !important;
    font-weight: bold;
    font-size: 1.1em !important;
    font-family: 'Arial';
}

.diff-type{
    background-color: var(--hover-background) !important;
}

.diff-type > li > a {
    color: var(--main-text) !important;
}



/* Compare Window */
#diffHeader {
    background-color: var(--main-background) !important;
}

.skip {
    background-color: var(--second-background) !important;
    border-style: none !important;
}

table.diff tbody th {
    background-color: var(--second-background) !important;
}

table.diff tbody .equal {
    background-color: lightgrey !important;
}
}
