css:
@-moz-document domain("web.roblox.com"), domain("www.roblox.com") {
#header, .rbx-header, header, .header-wrap, #SponsoredPage .header, nav.header {
    background-color: #252525 !important;
    background-attachment: fixed !important;
    background-position: center left !important;
    background-size: contain !important;
    background-repeat: repeat-x !important;
    opacity: 1 !important;
}
}

@-moz-document domain("web.roblox.com"), domain("www.roblox.com") {
.bd-content, .bd-toc-wrap, .home-inner, #main-outlet, #navigation, .notification-content-view, .dropdown-menu, .menu-vertical, .nav.nav-tabs a, .stack-row, .section-content-off, .store-card, div.collapsed-sidebar, .bd-sidebar-wrap, #chat-main, .dialog-container, .message-news, .roblox-message-row, .default-error-page, .white-container, .page-details, .roblox-popover-container, .slide-items-container, .card-with-game, .body-width, .has-limit, .swagger-ui-wrap .content, div.tab-active, .btr-explorer, .profile-avatar-right .profile-avatar-mask, .body-container, .menu-secondary-container, .alert-info, .rbx-divider, .btr-thumb-btn-container .btr-thumb-popup, .blurbs-container, .game-list-container, .premium-card, .perks {
    background: #383838 !important;
    color: var(--text) !important;
}
}
