css:
@-moz-document domain("bbs.io-tech.fi") {
:root {
    --white: #fff;
    --black: #000;
    --var-transparent: transparent;
    
    --bg-color-1: #1B1A19;
    --bg-color-2: #201F1E;
    --bg-color-3: #292929;
    --bg-color-4: #0C1319;
    --bg-color-5: #181818;
    --bg-color-6: #111111;
    --txt-color-1: #C8C6C4;
    --txt-color-2: #E1DFDD;
    --txt-color-3: #A19F9D;
    --txt-color-4: #8A8886;
    --txt-color-5: #979693;
    --txt-gray-color-1: #888;
    --txt-gray-color-2: #444;
    --txt-gray-color-3: #ddd;
    --link-hover-color-1: #E1DFDD;
    --link-hover-color-2: #D2D0CE;
    --link-external-color: #00AACC;
    --border-color-1: #383838;
    --border-color-2: #605E5C;
    --border-color-3: #323130;
    
    --brand-color: #2dbf5b;
    --warnings-border-color: #94532D;
    --warnings-bg-color: #B1470E;
    --warnings-txt-color: #FBBC98;
}

body,
.message-body {
    font-family: Arial, "Segoe UI", "Helvetica Neue", Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, "Fira Sans", "Droid Sans", sans-serif !important;
}

body {
    background-color: var(--bg-color-2);
    color: var(--txt-color-1);
}

body h1:first-child {
    color: var(--txt-color-2);
}

.userBanner strong {
    font-size: 105% !important;
    font-weight: 500 !important;
}

.p-nav {
    background-color: var(--bg-color-2) !important;
    border-bottom: 2px solid var(--border-color-1) !important;
}

.p-title-value {
    font-size: 1.3em !important;
}

.node.node--unread .node-title a,
.overlay-title,
.block-formSectionHeader {
    color: var(--txt-color-1) !important;
}

.node.node--unread .node-title a:hover,
.is-unread .structItem-title a:hover,
.structItem-title a:hover {
    color: var(--link-hover-color-1) !important;
}

.node-body .node-icon {
    background-color: var(--bg-color-2) !important;
    padding: 10px !important;
}

.structItem-title a,
.subNodeLink--unread,
.node--unread .node-icon i:before,
.subNodeLink.subNodeLink--unread:before,
.node-icon i:before {
    color: var(--txt-color-1) !important;
}

.node:not(.node--unread) .node-title a,
.node:not(.node--unread) .node-icon i:before {
    color: var(--txt-color-3) !important;
}

.subNodeLink:not(.subNodeLink--unread),
.subNodeLink:not(.subNodeLink--unread):before {
    color: var(--txt-color-3) !important;
}

.subNodeLink:not(.subNodeLink--unread):hover {
    color: var(--link-hover-color-2) !important;
}

.is-unread .structItem-title a {
    font-weight: 400 !important;
    color: var(--link-hover-color-2) !important;
}

.is-unread .structItem-title:before {
    content: "\2605";
    color: var(--link-hover-color-2) !important;
}

.structItem:not(.is-unread) .structItem-cell .structItem-title a {
    color: var(--txt-color-3) !important;
}

.structItem:not(.is-unread) .structItem-cell .structItem-title a:hover {
    color: var(--txt-color-1) !important;
}

.structItem-cell.structItem-cell--iconIcon i {
    color: var(--txt-color-3) !important;
}

.structItemContainer-group:not(.structItemContainer-group--sticky) .structItem.structItem--thread:hover .structItem-cell--icon:first-child {
    background-color: var(--bg-color-2) !important;
}

.structItem.structItem--thread:hover {
    background-color: var(--bg-color-3) !important;
}

.is-unread .structItem-cell.structItem-cell--iconIcon i {
    color: var(--txt-color-1) !important;
}

.p-description {
    color: var(--txt-color-3) !important;
}

.message-name {
    padding-bottom: 5px !important;
}

.message-userExtras,
.message-userExtras dt {
    background-color: var(--bg-color-3) !important;
    color: var(--txt-color-3) !important;
    margin-top: 0 !important;
}

.message-userExtras {
    border-left: 4px solid var(--border-color-1);
    padding: 10px 5px 3px 5px !important;
}

.message-userDetails {
    padding-top: 5px;
    padding-bottom: 0 !important;
    margin-bottom: 0 !important;
    background-color: var(--transparent) !important;
    color: var(--txt-color-3) !important;
}

.message-avatar {
    padding-top: 15px !important;
    background-color: var(--transparent) !important;
}

.message.message--post {
    border: 0 !important;
}

.userTitle.message-userTitle {
    padding-bottom: 5px !important;
    font-weight: 500 !important;
}

.fa--xf.fal.fa-asterisk:before {
    content: "";
}

.node-main > .node-description {
    color: var(--txt-color-3) !important;
    font-size: 85% !important;
}

.node-extra-row a {
    color: var(--txt-color-1) !important;
    font-weight: 400 !important;
}

.node-extra-row a:hover {
    color: var(--link-hover-color-1) !important;
}

.node-extra-row > ul li,
.node-extra-row .node-extra-user a {
    color: var(--txt-color-3) !important;
    font-weight: 500 !important;
}

.node-extra-row .node-extra-user a:hover {
    color: var(--txt-color-1) !important;
}

.message-main {
    padding: 0 !important;
    margin: 0 !important;
}

.message-cell--main {
    padding: 10px !important;
    background: rgb(27,26,25) !important;
    background: linear-gradient(0deg, rgba(27,26,25,1) 0%, rgba(32,31,30,1) 33%, rgba(32,31,30,1) 100%) !important;
}

.message-cell.message-cell--user,
.message-cell--user {
    padding: 0 !important;
    border-right: 1px solid var(--bg-color-3) !important;
    background: rgb(27,26,25) !important;
    background: linear-gradient(0deg, rgba(27,26,25,1) 0%, rgba(32,31,30,1) 33%, rgba(32,31,30,1) 100%) !important;
}

.message-newIndicator {
    background-color: var(--brand-color) !important;
    color: var(--bg-color-4);
}

.message-newIndicator:before {
    content: "\2605";
    margin-right: 3px !important;
    vertical-align: top !important;
}

.node-body .node-main .node-title a {
    font-weight: 500 !important;
}

.node-body .node-main .node-title a:hover {
    color: var(--link-hover-color-1) !important;
}

.node--unread .node-title,
.p-body-sideNav .block-header,
.p-body-sideNav .block-minorHeader {
    font-weight: 500 !important;
}

.block-header .block-header--left {
    font-weight: 500 !important;
}

.block-header {
    background: var(--border-color-3) !important;
    color: var(--txt-color-1) !important;
}
    
.subNodeLink.subNodeLink--unread {
    font-weight: 400 !important;
}

.p-nav--wrapper {
    position: static !important;
}

.p-body-main,
.p-body-header,
.p-body-inner {
    background-color: var(--bg-color-1) !important;
}

.block-filterBar {
    background-color: var(--bg-color-2) !important;
    border-bottom: 1px solid var(--border-color-3) !important;
}

.block-filterBar .filterBar a {
    color: var(--txt-color-3) !important;
    font-weight: 500 !important;
    background-color: var(--bg-color-3) !important;
}

.block-filterBar .filterBar a:hover {
    background-color: var(--bg-color-2) !important;
}

.filterBar-filterToggle {
    background-color: var(--border-color-3) !important;
}

.tabs-tab.is-active {
    border-bottom: 3px solid var(--border-color-2) !important;
    color: var(--txt-color-1) !important;
}

.tabs-tab {
    color: var(--txt-color-3) !important;
}

.tabs-tab:hover {
    border-bottom: 3px solid var(--txt-color-3) !important;
    color: var(--link-hover-color-1) !important;
}

.structItem-cell.structItem-cell--meta,
.structItem-cell.structItem-cell--meta dl dt,
.structItem-cell.structItem-cell--meta dl dd,
.structItem-cell.structItem-cell--latest,
.structItem-cell.structItem-cell--latest a {
    color: var(--txt-color-3) !important;
}

@media only screen and (min-width: 901px) {
    .structItem-cell.structItem-cell--meta {
        width: 165px !important;
    }
}

@media only screen and (max-width: 900px) {
    .structItem-cell.structItem-cell--meta {
        width: 135px !important;
    }
}

.structItem-cell.structItem-cell--latest a:hover {
    color: var(--link-hover-color-1) !important;
}

article.message {
    background-color: var(--bg-color-2) !important;
}

.block--messages .message+.message, .block--messages .block-row+.message, 
.block--messages .message+.block-row, .block--messages .block-row+.block-row {
    margin-top: 2px !important;
    border: 0 !important;
}

.block--messages .message,
.p-footer a,
.p-footer h3,
.p-footer-custom .xb-footer-block .block-container .block-body,
.structItem-status--watched:before {
    color: var(--txt-color-1) !important;
}

.block-textHeader {
    color: var(--txt-color-3) !important;
    font-weight: 500 !important;
}

.structItemContainer-group--sticky {
    background-color: var(--bg-color-1) !important;
    border-bottom: 4px dashed var(--border-color-3) !important;
}

.structItemContainer-group:not(.structItemContainer-group--sticky) .structItem-cell--icon:first-child,
.structItemContainer .structItem-cell--icon:first-child {
    background-color: var(--bg-color-1) !important;
}

.structItemContainer-group--sticky .structItem-cell--icon:first-child {
    background-color: var(--bg-color-2) !important;
}

.structItemContainer-group:not(.structItemContainer-group--sticky) {
    background-color: var(--bg-color-2) !important;
}

.structItemContainer-group.structItemContainer-group--sticky:after {
    display: none !important;
}

.structItem--thread {
    border-top: 1px solid var(--border-color-3) !important;
}

.block-body {
    background-color: var(--bg-color-1) !important;
}

.rssfeed-date,
.listInline.listInline--bullet li {
    color: var(--txt-color-3) !important;
}

.p-sectionLinks {
    background-color: var(--bg-color-1) !important;
    border-bottom: 0 !important;
}

.p-sectionLinks-list {
    color: var(--txt-color-1) !important;
}

.p-navSticky {
    position: static !important;
}

.p-navEl {
    text-transform: uppercase !important;
}

.navhome .p-navEl-link  {
    color: var(--brand-color) !important;
    font-weight: 600;
}

.navhome .p-navEl-link:hover  {
    color: var(--link-hover-color-1) !important;
}

.p-navEl.is-selected a {
    background-color: var(--border-color-3) !important;
    color: var(--txt-color-1) !important;
}

.p-nav-list .p-navEl.is-selected {
    background-color: var(--border-color-3) !important;
}

.p-navgroup.p-account.p-navgroup--member {
    background-color: var(--border-color-3) !important;
}

.message-header .message-attribution,
.block-header-wrapper > h2,
.p-body-sideNav .block-header {
    background-color: var(--border-color-3) !important;
    color: var(--txt-color-1) !important;
}

.message-attribution ul li a:hover,
.message-attribution a:hover {
    background-color: var(--transparent) !important;
    color: var(--white) !important;
}

.p-footer-custom {
    background-color: var(--bg-color-1) !important;
}

.reactionsBar {
    border: 0 !important;
}

.reactionsBar a,
.message-lastEdit,
.message-footer .message-actionBar a {
    color: var(--txt-color-4) !important;
}

.bbCodeBlock-expandContent {
    color: var(--txt-color-1) !important;
    font-size: 95% !important;
}

.message-signature .bbWrapper,
.structItem-minor,
.structItem-minor a,
.structItem-minor .structItem-pageJump a {
    color: var(--txt-color-5) !important;
    font-size: 90% !important;
    font-weight: 500 !important;
}

.message-content.js-messageContent {
    display: flex !important;
    flex-flow: column nowrap !important;
    justify-content: space-between !important;
}

.message-signature {
    border: 0 !important;
    color: var(--txt-color-4) !important;
}

.reactionsBar a:hover {
    color: var(--txt-color-1) !important;
}

.reactionsBar img,
.reactionSummary {
    display: none !important;
}

.reactionsBar .reactionsBar-link:before {
    content: "\2713";
    font-weight: 700 !important;
    margin-right: 5px !important;
}

.reactionsBar .reactionsBar-link:hover {
    color: var(--txt-color-1) !important;
}

.reaction.has-reaction img {
    display: none !important;
}

.reaction.has-reaction .reaction-text,
.reaction.has-reaction:before {
    color: var(--brand-color) !important;
    font-weight: 500 !important;
    font-size: 90%;
    content: "\2713";
}

.XenBase .block--messages .message .message-content .bbCodeBlock-title {
    background-color: var(--bg-color-5) !important;
}

.XenBase .block--messages .message .message-content .bbCodeBlock-title a {
    color: var(--txt-color-1) !important;
}

.XenBase .block--messages .message .message-content .bbCodeBlock-title a:before {
    content: "\21EA";
    margin-right: 3px !important;
    margin-top: -1px !important;
}

.XenBase .block--messages .message .message-content .bbCodeBlock-title a:hover {
    color: var(--link-hover-color-1) !important;
    text-decoration: none !important;
}

.bbCodeBlock-content {
    background-color: var(--bg-color-1) !important;
    color: var(--txt-color-1) !important;
}

.message-footer .message-actionBar {
    border-top: 0 !important;
    padding-top: 2px !important;
    padding-bottom: 5px !important;
}

.message-footer .message-actionBar a:hover {
    color: var(--txt-color-1) !important;
}

.bbCodeBlock.bbCodeBlock--screenLimited.bbCodeBlock--code,
.bbCodeBlock--expandable,
.bbCodeBlock--unfurl,
.bbCodeBlock--unfurl .fauxBlockLink,
.bbCodeBlock--spoiler {
    border: 0 !important;
    border-left: 4px solid var(--txt-gray-color-1) !important;
    background-color: var(--bg-color-2) !important;
}

.block--messages {
    margin-bottom: 0 !important;
}

.bbWrapper .fr-top {
    border-top: 2px solid var(--border-color-2) !important;
    margin-top: 10px !important;
}

.bbWrapper b {
    font-weight: 700 !important;
}

.show-placeholder .fr-placeholder {
    color: var(--txt-color-4) !important;
}

.p-title-pageAction a,
.buttonGroup .button--link.button,
.formButtonGroup-extra a,
.formButtonGroup button,
.formSubmitRow-main button,
.button--link,
.menu-footer-controls button,
.menu-footer-controls a.button,
.js-pageJumpGo.button,
.button--icon--quote {
    background-color: var(--bg-color-2) !important;
    border: 1px solid var(--border-color-3) !important;
}

.bbCodeSpoiler button,
.p-title-pageAction a:hover,
.buttonGroup .button--link.button:hover,
.formButtonGroup-extra a:hover,
.formButtonGroup button:hover,
.formSubmitRow-main button:hover,
.button--link:hover,
.menu-footer-controls button:hover,
.menu-footer-controls a.button:hover,
.pageNavSimple-el.pageNavSimple-el--current:hover,
.js-pageJumpGo.button:hover,
.button--icon--quote:hover {
    background-color: var(--border-color-3) !important;
}

.pageNavSimple-el.pageNavSimple-el--current {
    background-color: var(--bg-color-2) !important;
    color: var(--txt-color-1) !important;
    border: 1px solid var(--border-color-3) !important;
}

.buttonGroup a {
    margin: 0 0 3px 3px !important; 
}

.inputNumber-button {
    color: var(--border-color-2) !important;
}

.inputNumber-button:hover {
    background-color: var(--bg-color-2) !important;
    color: var(--border-color-2) !important;
}

.p-title-pageAction .button,
.p-body-main .button,
.button--scroll.button {
    color: var(--border-color-2) !important;
    background-color: var(--bg-color-2) !important;
    border-color: var(--bg-color-2) !important;
}

.p-title-pageAction .button:hover,
.p-body-main .button:hover,
.button--scroll.button:hover {
    color: var(--txt-color-3) !important;
    background-color: var(--border-color-3) !important;
    border-color: var(--border-color-3) !important;
}

.menu-footer-controls button,
.menu-footer-controls a.button,
.js-pageJumpGo.button {
    border-color: var(--border-color-3) !important;
}

.bbCodeSpoiler-button:not(.is-active) {
    background-color: var(--border-color-3) !important;
    border-color: var(--border-color-2) !important;
}

.bbCodeSpoiler-button.is-active {
    background-color: var(--txt-gray-color-2) !important;
    border-color: var(--txt-gray-color-2) !important;
}

.bbCodeSpoiler-button.is-active:before {
    content: "\25BC" !important;
    color: var(--txt-color-1) !important;
    font-size: 90% !important;
    margin-right: 5px !important;
}

.bbCodeSpoiler-button.is-active .button-text,
.menu-footer-controls button {
    color: var(--txt-color-1) !important;
}

.bbCodeSpoiler-button:not(.is-active):hover {
    border-color: var(--border-color-2) !important;
    background-color: var(--border-color-3) !important;
}

.avatar,
.avatar.avatar--xxs.avatar--default,
.avatar.avatar--xs.avatar--default,
.avatar.avatar--s.avatar--default,
.avatar.avatar--m.avatar--default,
.message-avatar-wrapper a,
.structItem-iconContainer a,
.memberTooltip-avatar a {
    background-color: var(--bg-color-1) !important;
    color: var(--txt-color-4) !important;
}

.avatar {
    border: 2px solid var(--bg-color-3) !important;
    border-radius: 0 !important;
}

.message-avatar-wrapper {
    padding-top: 0 !important;
}

.p-body-inner .p-breadcrumbs--parent .p-breadcrumbs {
    background-color: var(--bg-color-2) !important;
}

.p-body-inner .p-breadcrumbs--bottom {
    position: sticky !important;
    bottom: -1px !important;
    z-index: 100 !important;
    width: 100% !important;
}

.pageNav a,
.p-breadcrumbs a,
.button-text,
.bbWrapper .bbCodeBlock-expandLink a {
    color: var(--txt-color-3) !important;
    font-weight: 600 !important;
}

.pageNav a:hover,
.p-breadcrumbs a:hover,
.button-text:hover,
.bbWrapper .bbCodeBlock-expandLink a:hover {
    color: var(--txt-color-1) !important;
}

.pageNav-page--current a {
    background-color: var(--border-color-3) !important;
}

.pageNav-page.pageNav-page--current {
    border: 0 !important;
    /* clear */
    border: 1px solid var(--txt-gray-color-2) !important;
    background: var(--transparent) !important;
}

.pageNav-page, .pageNav-jump {
    background: var(--bg-color-2) !important;
    border-color: var(--border-color-3) !important;
}

.bbWrapper .username,
.bbWrapper .link.link--internal,
.bbWrapper .link.link--external,
.bbWrapper > a,
.bbWrapper .bbCodeBlock-expandContent a,
.attachment-name a,
.fr-wrapper a,
.fauxBlockLink-linkRow.u-concealed {
    color: var(--link-external-color) !important;
}

.bbWrapper .bbCodeBlock-expandContent .username {
    color: #fafafa !important;
    font-weight: 500;
}

.bbWrapper .link.link--external:after {
    content: "\29C9" !important;
    font-size: 85% !important;
    margin-right: 0.3em !important;
    margin-left: 0.2em !important;
    text-decoration: none !important;
}

.bbWrapper .link.link--external:hover:after {
    text-decoration: none !important;
}

.message-body .bbWrapper {
    color: var(--link-hover-color-2) !important;
}

.message-body .bbWrapper br {
    content: "";
    margin: 2em !important;
    display: block !important;
    font-size: 24% !important;
}

.xb-footer-block.xb-footer-block-1 {
    color: var(--txt-color-1) !important;
    font-weight: 500 !important;
}

.xb-footer-block.xb-footer-block-1 img {
    opacity: 0.8 !important;
}

.bbCodePreview {
    margin-left: 20px !important;
}

.p-navEl.is-menuOpen a {
    color: var(--txt-gray-color-3) !important;
}

.blockStatus {
    border-left: 3px solid var(--border-color-2);
}

.formButtonGroup-primary {
    margin-left: 5px !important;
}

.message-footer {
    margin-top: 10px !important;
    border-top: 1px solid var(--border-color-2) !important;
}

.message-footer:after {
    clear: both !important;
}

.message-actionBar.actionBar {
    margin: 0 !important;
    float: left !important;
}

.message-footer .reactionsBar {
    padding-top: 7px !important;
    float: right !important;
    margin-top: 0 !important;
    background-color: var(--transparent) !important;
}

.menu .menu-header {
    background-color: var(--border-color-3) !important;
    color: var(--txt-color-1) !important;
}

.p-navgroup-link.is-menuOpen {
    color: var(--txt-color-1) !important;
}

.blockLink.is-selected {
    border-color: var(--txt-gray-color-1) !important;
}

.blockLink {
    border-left: 3px solid var(--transparent) !important;
}

.menu-linkRow:hover,
.blockLink:hover {
    border-left: 3px solid var(--border-color-2) !important;
}

.p-pe-copyright {
    color: var(--txt-color-1) !important;
}

.contentRow-main a:hover {
    color: var(--txt-color-2) !important;
}

.contentRow-minor {
    color: var(--txt-gray-color-1) !important;
}

.contentRow-minor a {
    color: var(--txt-color-1) !important;
}

.p-body-pageContent .block .block-container.lbContainer {
    border: 0 !important;
}

.block-container .block-body .node-body {
    background-color: var(--transparent) !important;
}

.block-container .block-body .node:hover {
    background-color: var(--bg-color-2) !important;
}

.tooltip-content .block-minorHeader,
.p-body-inner .block-container > h3.block-minorHeader {
    color: var(--txt-color-1) !important;
