css:
@-moz-document domain("youtube.com") {
/* Applies to: "URLs on the domain": youtube.com */

/* remove home page */
ytd-two-column-browse-results-renderer[page-subtype=home] {
    display: none !important;
}

/* remove link to trending page */
a[href="/feed/trending"] {
    display: none !important;
}

/* remove link to originals page */
a[href="/originals"] {
    display: none !important;
}

/* remove recommendations */
ytd-watch-next-secondary-results-renderer {
    display: none !important;
}

/* remove recommendations from the end of the video */
.ytp-endscreen-content {
    display: none !important;
}
}
