css:
@-moz-document domain(jsfiddle.net) {
#sidebar
  {
width:210px!important;
    left: -205px !important;
position: absolute;
z-index:99999!important;
height:100%;
background: #1d2127;
top:0!important;
  }

  #sidebar:hover
  {
    left: 0px !important;
  }
  #content
  {
z-index:0;
grid-area:unset;
    width: 100%;
    /*margin-left: 15px !important;*/
  }

#editor .panel-v:first-child {
width:45%;
}

#layout-container {
display: flex;
grid-template-columns: unset; 
 grid-template-areas: unset;
}

.windowSettings {
z-index:999999!important;
left:20px!important;
}

.windowLabelCont {
left: 10px!important;
}
.windowLabelCont .panelTidy, .windowLabelCont .windowLabel {
padding: 0 10px !important;
}
.CodeMirror-scroll {
left: -10px!important;

}
.windowSettings:after, .windowSettings:before {
right: unset!important;
}
.CodeMirror-linenumber {

padding-right: 3px!important;
padding-left: 0px!important;
}


#tabs .tabItem.active {
box-shadow: 0 1px 0 #21262d !important;
background: #21262d;
}

}
