@-moz-document domain("podcasts.google.com"), domain("podcasts.google.com/") {
:root {
	--primary: rgb(29, 29, 29);
	--secondary:rgb(45, 45, 45);
	--title: #fff;
	--subcontent: #e8e8e8;
	--links: #e1e1e1;
	--button: #555;
	--sidebar: #e8e8e8;
}

/* Auto dark theme */
@media (prefers-color-scheme: dark) {
	body {
		background-color: var(--primary)!important;
	}

/* Top bar */
.gb_ta, .gb_4a, .gb_7e, .gb_Oc {
	background-color: var(--secondary)!important;
}

a {
	color: var(--subcontent)!important;
	font-weight: 700!important;
}

p, .yFWEIe, .BpVHBf, .OTAikb, .LrApYe, .ALlaKf, .MBPL8b, .oG0wpe, .QpaWg, .yuTZxb, .tX5qm, .u0Mk7d, .JL0hbc {
	color: var(--subcontent)!important;
	font-weight: 400!important
}

/* Headings and other big content */
h1, h2, h3, h4, h5, h6, .eWeGpe, .ZfMIwb, .e3ZUqe, .OTz6ee, .PGfkhc, .TzqEyd, .wv3SK, .PRPYJc, .Mji2k, .eI5bkf, .VGALGe, .tsXz9, .ldf2Je, .Ut8Gr, .FyxyKd, .SuNkYe, .tdELcf, .VPytgf, .NoOODc {
	color: var(--title)!important;
	font-weight: 900!important;
}

/* Tap to go right arrow button */
.zJw79d, .f4MdMc, .JSLBqe, .pyK9td {
	background-color: var(--button)!important;
	border: 0!important;
	color: var(--title)!important;
	font-weight: 700!important;
}

/* Search bar */
.Ax4B8, .yNVtPc, .gb_8e {
	background-color: var(--button)!important;
	color: var(--title)!important;
	font-weight: 500!important;
}
	
/* Media Player */
.d5Ncgc, .DPvwYc {
	background-color: var(--secondary)!important;
	color: var(--title)!important;
	}
	
.zQTmif,.SSPGKf {
background-color: var(--secondary)!important;
	}

.ugPjk  {
	color: var(--title)!important;	
	}
}
}
