css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("thedailywtf.com"){

.ArticleContainer table[class*="Navigation"],
.ArticleContainer~table[class*="Navigation"],
body > div:not(#PrimaryOuter),
div[class$="_Inner"]>img[alt=""]:first-child,
#MainContent .ArticleContainer+.ArticleFooter,
#MainContent>div[class*="DisplayOptions"],
blockquote>div:first-child>img:first-child,
.CommentSubHeading span[id$="_UnregisteredIndicator"],
#Trackbacks
{display:none!important}

body,
.glow,
#PrimaryOuter,
#Primary,
#MainContent,
h2,
.ArticleBody,
.ArticleFooter,
div[class*="ArticleSummary"],
div[class$="_Outer"],
div[class$="_Outer"] *,
div.ArticleAuthor,
div.ArticleAuthor *,
.CommentButtons *
{float:none!important;
clear:both!important;
width:auto!important;
height:auto!important;
border:0!important;
margin:0!important;
padding:0!important;
min-height:0!important;
background:none!important;
color:inherit!important;
font:inherit!important;
line-height:inherit!important}

body
{background:inherit!important;
padding:0.5em 1em!important}

div[class$="_Outer"],
div[class$="_Outer"] *,
div.ArticleAuthor,
div.ArticleAuthor *
{background:none!important;
color:inherit!important;}

div[class$="_Outer"],
div[class$="_Outer"] *,
div[class$="_Outer"] + .ArticleAuthor,
div[class$="_Outer"] + .ArticleAuthor *
{display:inline!important;}

div[class$="_Outer"] a,
.ArticleContainer h1
{font-size:150%!important}

div[class$="_Outer"] a:link,
div[class$="_Outer"] a:visited,
.ArticleAuthor a,
.CommentButtons a
{color:rgb(2,70,155)!important}

div[class$="_Outer"] a[href]
{font-weight:bold!important}

.ArticleFooter
{margin-bottom:1em!important;
padding-bottom:1em!important;
border-bottom:thin solid #ccc!important}

blockquote,
blockquote blockquote blockquote,
blockquote blockquote blockquote blockquote blockquote
{background:#eee!important;
border-width:0!important;
margin-bottom:.5em!important}
blockquote blockquote,
blockquote blockquote blockquote blockquote
{background:#dedede!important}
blockquote blockquote blockquote blockquote blockquote blockquote
{border:thin solid gray!important}

.ArticleBody blockquote
{background:none!important}

.CommentContainer,
div[class^="CommentHead"], 
div[class^="CommentHead"] *
{border:0!important;
margin:0!important;
padding:0!important}

div[class^="CommentHead"] td *
{display:inline!important}

.CommentButtons::before,
.CommentSubHeading::before
{content:"â€¢"!important}

.CommentButtons a
{text-transform:lowercase!important}

.ArticleBody img
{border:thin solid #ddd!important}

.ArticleBody blockquote img
{margin:0!important}

a:link,
a:visited
{text-decoration:none!important}

a:link:hover,
a:visited:hover
{text-decoration:underline!important}


.ArticleBody hr
{border:dotted #ddd!important;
border-width:thin 0 0 0!important}

} /* 20090517 */
