css:
@-moz-document domain("workbench.developerforce.com") {
/*Main color variables*/
:root
{
	/*User colors*/
	--main-background: #222222;
	--second-background: #323232;
	--hover-background: #444;
	--main-text: #bbb;
	--dimmer-text: #bbb;
    --button-background: #333;
}

body, #nav, .submenu,
#tabs, .instructions,
.submenu > ul > li,
.filter_plus_cell_0,
#source_container,
#source_object,
#ui-id-2,
#mainBlock
{
    color: var(--main-text) !important;
    background-color: var(--main-background) !important;
}

#default_object,
#QB_object_sel,
#QB_field_sel,
#soql_query_textarea,
#QB_orderby_field,
#QB_orderby_sort,
#QB_nulls,
#QB_limit_txt,
#QB_filter_field_0,
#QB_filter_compOper_0,
#QB_filter_value_0
{
    background-color: var(--button-background) !important;
    color: var(--dimmer-text) !important;
}

.sub {
    background-color: var(--button-background) !important;
    color: var(--dimmer-text) !important;
}

.sub > li > a:hover {
    background-color: var(--hover-background) !important;
}

.pseudoLink, a {
    color: white !important;
}
}
