css:
@-moz-document domain(loj.ac) {
    body {
        background-color: rgb(0, 0, 0);
    }

    .ui.menu {
        background: rgb(37, 37, 37);
    }

    .ui.menu .active.item {
        color: rgb(164, 164, 164);
        background: rgb(33, 33, 33);
    }

    .ui.menu .item {
        color: rgb(164, 164, 164);
    }

    .ui.segment {
        background: rgb(37, 37, 37);
    }

    .ui.attached.segment {
        border: rgb(37, 37, 37);
    }

    .ui.attached.block.header {
        background: rgb(50, 50, 50);
        border: rgb(50, 50, 50);
    }

    .ui.header {
        color: rgb(164, 164, 164);
    }

    .ui.table thead th {
        color: rgb(164, 164, 164);
    }

    .ui.table td {
        color: rgb(164, 164, 164);
    }

    .ui.form .inline.field>label,
    .ui.form .inline.field>p,
    .ui.form .inline.fields .field>label,
    .ui.form .inline.fields .field>p,
    .ui.form .inline.fields>label {
        color: rgb(164, 164, 164);
    }

    .ui.form .inline.field>.selection.dropdown,
    .ui.form .inline.fields .field>.selection.dropdown {
        background-color: rgb(140, 140, 140);
    }

    .ui.label {
        background: rgb(37, 37, 37);
        color: rgb(164, 164, 164);
    }

    .ui.attached.segment {
        color: rgb(164, 164, 164);
    }

    .ui.segment {
        border: 1px solid rgb(110, 110, 110);
    }

    .ui.block.header {
        background-color: rgb(50, 50, 50);
        border: rgb(50, 50, 50);
    }

    .ui.menu a.header.item:link {
        width: 122px;
        background-image: url('https://tth37.cn/images/custom-logo.svg') !important;
        background-position: center !important;
        background-size: 90px 30px !important;
        background-repeat: no-repeat !important;
    }

    .ui.menu .header.item img {
        display: none;
    }

    .ui.toggle.checkbox input:checked~.box:before,
    .ui.toggle.checkbox input:checked~label:before {
        opacity: 0.6;
    }

    .ui.toggle.checkbox input:focus~.box:before,
    .ui.toggle.checkbox input:focus~label:before {
        background-color: rgba(50, 50, 50, 0.9) !important;
    }

    #show_tag_button {
        color: rgb(164, 164, 164) !important;
    }

    .ui.menu .ui.dropdown .menu>.item {
        color: rgb(164, 164, 164) !important;
        background: rgb(37, 37, 37) !important;
    }

    .ui.buttons>.ui.dropdown:last-child .menu,
    .ui.menu .right.dropdown.item .menu,
    .ui.menu .right.menu .dropdown:last-child .menu {
        border: rgb(37, 37, 37) !important;
    }

    .ui.card {
        background-color: rgb(50, 50, 50) !important;
    }

    .ui.card>.content>.header:not(.ui),
    .ui.cards>.card>.content>.header:not(.ui) {
        color: rgb(164, 164, 164);
    }

    .ui.card .meta>a:not(.ui),
    .ui.cards>.card .meta>a:not(.ui) {
        color: rgb(164, 164, 164);
    }

    .ui.card>.extra a:not(.ui),
    .ui.cards>.card>.extra a:not(.ui) {
        color: rgb(164, 164, 164);
    }

    .ui.menu a.item:hover {
        color: rgb(100, 100, 100) !important;
        background: rgb(33, 33, 33) !important;
    }

    .ui.breadcrumb .section {
        color: rgb(164, 164, 164);
    }

    h1 {
        color: rgb(164, 164, 164);
    }

    a.black-link {
        color: rgb(164, 164, 164);
    }

    p {
        color: rgb(164, 164, 164);
    }

    .ui.comments .comment .author {
        color: rgb(164, 164, 164);
    }

    .ui.comments .comment .metadata>* {
        color: rgb(164, 164, 164);
    }

    a.ui.label {
        opacity: 0.7;
    }

    .ui.form input[type="checkbox"],
    .ui.form textarea {
        background: rgba(110, 110, 110, 0.5) !important;
    }

    .ui.form input[type="checkbox"],
    .ui.form textarea:focus {
        background: rgba(140, 140, 140, 0.5);
    }

    .ui.breadcrumb .icon.divider {
        color: rgba(164, 164, 164, 0.7);
    }

    img {
        opacity: 0.7;
    }

    code,
    li,
    .pl-n,
    .pl-p,
    .pl-sc {
        color: rgb(164, 164, 164);
    }

    .pl-c1 {
        color: rgb(100, 100, 100);
    }

    .pl-k {
        color: rgb(194, 17, 61);
    }

    .pl-cp,
    .pl-cpf {
        color: rgb(0, 149, 64);
    }

    .ui.styled.accordion,
    .ui.styled.accordion .accordion {
        background: rgb(37, 37, 37);
    }

    .ui.grid>.column:not(.row),
    .ui.grid>.row>.column {
        color: rgb(164, 164, 164);
    }

    .ui.accordion .accordion .active.content,
    .ui.accordion .active.content .animating.content {
        background: rgb(50, 50, 50);
    }

    button {
        opacity: 0.8 !important;
    }

    .ui.buttons .button {
        opacity: 0.8;
    }

    .ui.vertical.menu .active.item {
        background: rgb(70, 70, 70);
        border: rgb(50, 50, 50);
    }

    .ui.vertical.menu .item {
        background: rgb(50, 50, 50);
        border: rgb(50, 50, 50);
    }

    .ui.vertical.menu .item:hover {
        background: rgb(70, 70, 70);
        border: rgb(50, 50, 50);
    }

    .ui.form input[type="checkbox"],
    .ui.form textarea {
        background: rgba(110, 110, 110, 0.5);
    }

    .ui.form input[type="checkbox"],
    .ui.form textarea:focus {
        background: rgba(140, 140, 140, 0.5);
    }

    .ui.selection.dropdown {
        background: rgba(110, 110, 110, 0.5);
    }

    .ui.selection.dropdown .menu>.item {
        background: rgba(110, 110, 110, 0.5);
        border: 0px solid red;
    }

    .ui.selection.dropdown .menu>.item.active {
        background: rgba(70, 70, 70, 0.5);
        border: 0px solid red;
    }

    .ui.checkbox input[disabled]~.box:after,
    .ui.checkbox input[disabled]~label,
    .ui.disabled.checkbox .box:after,
    .ui.disabled.checkbox label {
        color: rgb(164, 164, 164);
    }

    lable {
        color: rgb(164, 164, 164);
    }

    h2,
    h3,
    h4 {
        color: rgb(164, 164, 164);
    }

    input[type="text"] {
        background: rgb(140, 140, 140) !important;
        color: black !important;
    }

    .ui.message {
        background: black;
    }

    .ui[class*="center aligned"].container:after {
        content: " Dark Style by tth37.";
        color: rgb(164, 164, 164);
    }
}
