css:
@-moz-document domain("deviantart.com") {
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"],
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div,
div[data-hook="deviation_std_thumb"]>div>div {
	height: 200px !important;
}

.theme-dark div[data-hook^="content_row-"]>div {
    background-color: var(--D2);
}
.theme-light div[data-hook^="content_row-"]>div {
    background-image: var(--L13);
}
.theme-light.light-green div[data-hook^="content_row-"]>div {
    background-color: var(--L2);
}

.daeditor-torpedo-grid div[data-hook^="content_row-"]>div,
.daeditor-torpedo-grid-item{
	height: 200px !important;
}

.daeditor-torpedo-grid-item > div{
	height: 190px !important;
}

div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div>div>img,
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div>img,
div.daeditor-torpedo-grid-item img{
    height: 200px !important;
    object-fit: contain !important;
}

div[data-hook^="content_row-"]>div {
    padding: 20px 0 !important;
}

}
