css:
@-moz-document domain("deviantart.com") {
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"],
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div,
div[data-hook="deviation_std_thumb"]>div>div {
	height: 200px !important;
}

.daeditor-torpedo-grid div[data-hook^="content_row-"]>div,
.daeditor-torpedo-grid-item{
	height: 200px !important;
}

.daeditor-torpedo-grid-item > div{
	height: 190px !important;
}

div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div>div>img,
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div>img,
div.daeditor-torpedo-grid-item img{
    height: 200px !important;
    object-fit: contain !important;
}

div[data-hook^="content_row-"]>div {
    padding: 20px 0 !important;
}

div[data-hook^="content_row-"]>div {
	height: 280px !important;
}

div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] section {
	margin-bottom:-90px;
}
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] section *{
	opacity: 1 !important;
    background: none !important;
}
.theme-light div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] section,
.theme-light div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] .user-link:not(:hover),
.theme-light div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] svg:not(:hover),
.theme-light.light-green div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] section,
.theme-light.light-green div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] .user-link:not(:hover),
.theme-light.light-green div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] svg:not(:hover){
    color: var(--L8);
}

div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div,
div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"]>div>div{
	background: none !important;
}

div[data-hook^="content_row-"] div[data-hook="deviation_std_thumb"] section h6{
	margin-bottom: 8px !important;
}
}
