css:
@-moz-document domain("boards.4channel.org") {
.bsa-cnt, #skellington {
    display: none !important;
}
body, :root.yotsuba-b .suboption-list > div:last-of-type {
    background: #0d1126 !important;
    color: #8f95b3 !important;
}
table.flashListing tr:nth-of-type(odd) {
    background: #121735 !important;
    color: #949bbb !important;
}
.reply-to-thread~div>div:first-child:not(#fourchanx-settings):not(.move), .show-new-thread-option~div>div:first-child:not(#fourchanx-settings):not(.move) {
    opacity: 0 !important;
}
#fourchanx-settings {
    padding: 25px !important;
}
hr {
    display: none;
}
.inline {
    background-color: rgba(255, 255, 255, 0.01) !important;
}
.field {
    background-color: rgb(26, 27, 29);
    color: rgb(197,200,198);
    border-color: rgb(40, 41, 42);
}
.field:disabled {
    background-color: rgb(26, 27, 29);
    color: rgb(197,200,198);
    border-color: rgb(40, 41, 42);
}
.field:focus, textarea:focus, input:not([type]):focus, input#qr-filename, .field.focus {
    color: rgb(197,200,198);
    border-color: rgb(40, 41, 42) !important;
}
.field:hover {
    color: rgb(197,200,198);
    border-color: rgb(40, 41, 42);
}
#qr.has-captcha textarea.field {
    height: 12em;
}
#char-count {
    background-color: rgb(26, 27, 29);
    color: rgb(197,200,198);
}
.rc-anchor-light {
    background-color: red !important;
}
#header-bar.dialog {
    background: #0d1126 !important;
    color: #d98518 !important;
}
#board-list, #boardNavDesktopFoot {
    color: #734307 !important;
}
:root.yotsuba-b .qr-link {
    border-color: rgb(8, 6, 23) rgb(8, 6, 23) rgb(0, 0, 8);
    background: linear-gradient(#262435, #171526) repeat scroll 0% 0% transparent;
}
:root.yotsuba-b .qr-link:hover {
    background: #1A1829;
}
:root.yotsuba-b #menu {
    color: rgb(179, 185, 215) !important;
}
a, a:visited, .button, .quotelink, a.replylink:not(:hover), div#absbot a:not(:hover), div.post div.postInfo span.postNum a:hover, .posteruid .hand:hover, #arc-list .quotelink {
    color: #d98518 !important;
}
.postMessage, .name, .postertrip {
    color: rgb(179, 185, 215) !important;
}
blockquote>span.quote {
    color: #88ae24;
}
:root.yotsuba-b.highlight-you .quotesYou > .reply {
    border-left: 3px solid rgba(202, 209, 242, .8) !important;
}
:root.yotsuba-b.highlight-own .yourPost > .reply, :root.yotsuba-b.highlight-own .yourPost.opContainer {
    border-left: 3px dashed rgba(202, 209, 242, .8) !important;
}
a:hover, s:hover, .subject, .boardTitle, .button:hover, #arc-list .quotelink:hover {
    color: #d98518 !important;
    text-shadow: 0 0 6px #d98518 !important;
}
div.reply {
    background: #111733 !important;
    border-bottom: 0px !important;
    border-right: 0px;
}
.reply:target {
    background: #192353 !important;
    border-bottom: 0px !important;
    border-right: 0px !important;
}
.dialog {
    background: #0d1126 !important;
    border-color: rgba(202, 209, 242, .8) !important;
}
.qphl {
    outline: 1px solid rgba(202, 209, 242, .8) !important;
}
input[type=checkbox] {
    opacity: .5 !important;
}
#styleSelector, #settings select, #index-options select {
    background: #0d1126 !important;
    color: #8f95b3 !important;
    border-color: rgba(202, 209, 242, .8) !important;
}
div#absbot {
    color: #8f95b3 !important;
}
}

