/*---- REPOSITION PANELS ----*/
#identity-popup {
/*margin-top: 0px !important;*/
margin-left: -22px !important;}

#editBookmarkPanel {
-moz-border-image: url(chrome://browser/skin/star-panel.png) 26 18 22 50 / 26px 18px 22px 50px repeat stretch !important;
margin-right: 297px !important;}

#editBookmarkPanel > vbox {
margin-left: -32px !important;
margin-right: 2px !important;}

#editBookmarkPanel > hbox {
margin-left: -34px !important;
margin-right: 2px !important;}


/*** LOCATION BAR/SEARCHBAR ADJUSTMENT ***/
#urlbar {margin-left: 31px !important;} /*-1*/
#urlbar-search-splitter {margin-right: 1px !important;}
#search-container {margin-right: 1px !important;}


/*---- LOCATION BAR -----*/
/* START CAP */
