padding-left: 3px !important;}

#urlbar[focused="true"]{
background:url("data:image/png;base64,") no-repeat left center !important;}

#urlbar[focused="true"]:-moz-system-metric(mac-graphite-theme) {
background:url("data:image/png;base64,") no-repeat left center !important;}

window:not([active="true"]) #urlbar {
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAcCAYAAACptnW2AAAAOElEQVQokdXIwQkAIAxD0e7qYmlXyyDxFKFHEQU//MuLOAuAfEOSItnRXcXM/Ber6h36hZKGj+0mRFLzyizX/jQAAAAASUVORK5CYII=") no-repeat left center !important;}

/* URLBAR BODY */
#urlbar .autocomplete-textbox-container {
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAcCAYAAACptnW2AAAAMElEQVQokWNgoAz4+/v/R8cMjx49+o+OGf5jAaOCAyZYVFT0Hx0z/P//PxkdE58WAIXmsvIvl7ScAAAAAElFTkSuQmCC") repeat-x center left !important;
padding: 6px 1px 6px 0px;}

#urlbar[focused="true"] .autocomplete-textbox-container {
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAcCAYAAACptnW2AAAAYklEQVQokWMI7Tzvio4ZQrvPd6FjhuDus2fQMUNi07z/cNwIoRmOXXnw/9jl+xB8BUIz/McCRgXpIXjz2af/N599/A+hP/2/8ezjf4aiSWv/F01ExQxJUy4+QMfYEwO2ZAMAae7nmnsArIAAAAAASUVORK5CYII=") repeat-x center left !important;}

#urlbar[focused="true"]:-moz-system-metric(mac-graphite-theme) .autocomplete-textbox-container {
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAcCAYAAACptnW2AAAAZ0lEQVQokWNIKqxxQccMyXm1neiYIT63+gw6ZsgurPifVVjxH0ZnFVb8Zzh19vx/dMzwHwsYFaSH4J37D//fuf/w/90Hj/7D2AxVje3/qxvb/1c1tv+vbur4X9XY/h97FGNNDNiSDQA4jNiygYsc7wAAAABJRU5ErkJggg==") repeat-x center left !important;}

window:not([active="true"]) #urlbar .autocomplete-textbox-container {
background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAcCAYAAACptnW2AAAAMUlEQVQokWNgoAwsWLDgPzpmePz48X90zPAfCxgVHDDBRYsW/UfHDP///09Gx8SnBQD/y7qQbZ4OXwAAAABJRU5ErkJggg==") repeat-x center left !important;}

/* ENDCAP */
#urlbar .autocomplete-history-dropmarker {
background:url("data:image/png;base64,") no-repeat right center !important;
list-style-image: none !important;
margin-top: 0px !important;
margin-left: -8px !important;
margin-right: 1px !important;
padding-right: 4px !important;}

#urlbar[focused="true"] .autocomplete-history-dropmarker {
background:url("data:image/png;base64,") no-repeat right center !important;}

#urlbar[focused="true"]:-moz-system-metric(mac-graphite-theme) .autocomplete-history-dropmarker {
background:url("data:image/png;base64,") no-repeat right center !important;}

window:not([active="true"]) #urlbar .autocomplete-history-dropmarker {
background:url("data:image/png;base64,") no-repeat right center !important;}

#identity-box[open="true"],
#identity-box:active:hover {margin-bottom:-2px !important;}


/*---- STAR BUTTON ----*/
#star-button {
padding-right: 2px !important;
margin: -6px 0px -6px -31px !important;
list-style-image: url("data:image/png;base64,") !important;
-moz-image-region: rect(0, 16px, 0, 16px) !important;}

#urlbar[focused="true"] #star-button {
list-style-image: url("data:image/png;base64,") !important;}

#urlbar[focused="true"]:-moz-system-metric(mac-graphite-theme) #star-button {
list-style-image: url("data:image/png;base64,") !important;}

/* PRESSED */
#star-button:hover:active,
#urlbar:-moz-system-metric(mac-graphite-theme) #star-button:hover:active {
list-style-image: url("data:image/png;base64,") !important;}

/* STARRED */
#star-button[starred="true"] {
list-style-image: url("data:image/png;base64,") !important;}

#urlbar:-moz-system-metric(mac-graphite-theme) #star-button[starred="true"] {
list-style-image: url("data:image/png;base64,") !important;}

#urlbar[focused="true"] #star-button[starred="true"] {
list-style-image: url("data:image/png;base64,") !important;}

#urlbar[focused="true"]:-moz-system-metric(mac-graphite-theme) #star-button[starred="true"] {
list-style-image: url("data:image/png;base64,") !important;}

/* WINDOW INACTIVE */
window:not([active="true"]) #star-button,
window:not([active="true"]) #urlbar:-moz-system-metric(mac-graphite-theme) #star-button {
list-style-image: url("data:image/png;base64,") !important;}


/*---- RSS BUTTON ----*/
#feed-button[feeds] {
list-style-image: none !important;
background-image: url("data:image/png;base64,") !important;
-moz-image-region: rect(0, 0, 0, 0) /*rect(0px, 27px, 15px, 0px)*/ !important;
margin-bottom: -2px !important;
padding-bottom: 2px !important;

background-repeat: no-repeat !important;
/*margin-bottom: 1px !important;*/
padding-left: 25px !important;
padding-bottom: 16px !important;}

#feed-button[feeds]:active {
/*list-style-image:*/ background-image: url("data:image/png;base64,") !important;

/*-moz-image-region: rect(0px, 27px, 15px, 0px) !important;*/}


/*---- SEARCHBAR ----*/
/* DROPMARKER */
.searchbar-engine-button .searchbar-dropmarker-image {
margin-top: -1px !important;
margin-right: 1px !important;
padding-left: 1px !important;}
/* SHOW GREEN DROPMAKER */
.searchbar-engine-button:not([addengines="true"]) .searchbar-dropmarker-image {opacity: 0 !important;}

/* SEARCHBAR BODY */
.searchbar-textbox {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAcCAYAAACgXdXMAAAAJ0lEQVQImWNgQAX+/v7/GR4+fPif4f///7QkioqK/jP8//8/GWE3AN/2VvjkctAiAAAAAElFTkSuQmCC") repeat-x !important;}
/* INACTIVE */
window:not([active="true"]) .searchbar-textbox {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAcCAYAAACgXdXMAAAAKElEQVQImWNgQAVz5879z/Do0aP/DP///6clsWDBgv8M////T0bYDQBbiVhvIrG99QAAAABJRU5ErkJggg==") repeat-x !important;}

/* SEARCH LEFT */
.searchbar-engine-button {
background: url("data:image/png;base64,") no-repeat !important;}

/* INACTIVE */
window:not([active="true"]) .searchbar-engine-button {
background: url("data:image/png;base64,") no-repeat !important;}

/* SEARCH RIGHT */
.search-go-container {
background: url("data:image/png;base64,") no-repeat right top !important;}

/* INACTIVE */
window:not([active="true"]) .search-go-container {
background: url("data:image/png;base64,") no-repeat right top !important;}

/* FOCUSED */
#searchbar textbox[focused="true"] .textbox-input-box {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAcCAYAAACgXdXMAAAAPklEQVQImWMI7TzvyhDafb6LIbj77BmGpKb5/xmOXXnwn+H///80Im48/fifoWji2v8QK0O7z3cxhHaedwUAhsZhVJWryeIAAAAASUVORK5CYII=") repeat-x !important;}

#searchbar textbox[focused="true"]:-moz-system-metric(mac-graphite-theme) .textbox-input-box {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAcCAYAAACgXdXMAAAAPUlEQVQImWNIKqxxYUjOq+1kiM+tPsOQVVjxn+HU2fP/Gf7//08j4s79h/8Zqhrb/0OsTM6r7WRIKqxxAQDC/l6GIQio8QAAAABJRU5ErkJggg==") repeat-x !important;}

textbox[focused="true"] .searchbar-engine-button {
background: url("data:image/png;base64,") no-repeat left center !important;}

textbox[focused="true"]:-moz-system-metric(mac-graphite-theme)  .searchbar-engine-button {
background: url("data:image/png;base64,") no-repeat left center !important;}

textbox[focused="true"] .search-go-container {
background: url("data:image/png;base64,") no-repeat right center !important;}

textbox[focused="true"]:-moz-system-metric(mac-graphite-theme)  .search-go-container {
background: url("data:image/png;base64,") no-repeat right center !important;}


/*---- FISSION ----*/
#urlbar[fission="fusion"] .progress-bar {-moz-margin-end: -8px !important;
background: url("data:image/png;base64,") 0 -1px !important;
margin-top: -2px !important;
border-right: 1px solid #7da0d2 !important;}


#urlbar[fission="fusion"] > #statusbar-icon[value^="4"]:not([value="4"])  > .progress-bar,
#urlbar[fission="fusion"] > #statusbar-icon[value^="5"]:not([value="5"])  > .progress-bar,
#urlbar[fission="fusion"] > #statusbar-icon[value^="6"]:not([value="6"])  > .progress-bar {-moz-margin-end: -8px !important;
background: url("data:image/png;base64,") 0 -1px !important;
border-right: 1px solid #7da0d2 !important;}

#urlbar[fission="fusion"] > #statusbar-icon[value^="7"]:not([value="7"])  > .progress-bar,
#urlbar[fission="fusion"] > #statusbar-icon[value^="8"]:not([value="8"])  > .progress-bar {-moz-margin-end: -8px !important;
background: url("data:image/png;base64,") 0 -1px !important;
border-right: 1px solid #7da0d2 !important;}

#urlbar[fission="fusion"] > #statusbar-icon[value^="9"]:not([value="9"]) > .progress-bar {
background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAWCAYAAADjGu3TAAAAdElEQVQokaXJUQpDIQxE0dmq2YvrTFyBIIr6DEy/ClLSnzZw4JKBiDClRBHh3eCXg7vznEN359045zCC53kYwd6bEay1GMGckxGMMRhB750RtNYYQSmFkR8GM2Pkj6GUwruhqjQzqirvxufj3ai1MoKcMyMvQK7hBvhgMuIAAAAASUVORK5CYII=") 0 -1px !important;
border-right: 1px solid #cdcdcd !important;}
