css:
@-moz-document domain(youtubekids.com) {
  
::-webkit-scrollbar {
    display: none;
}


#top-row.ytk-masthead {
    background-color: #FFFFFF;
    display: flex;
    min-height: 32px;
    min-width: 640px;
    padding-top: 0;
    position: var(--ytk-masthead-ui-mode-position);
    z-index: 110;
}
#masthead.ytk-app {
    background-color: #FFFFFF;
    min-height: 0px;
    outline: none;
    top: 0;
    z-index: 101;
}
#secondary-results.ytk-two-column-watch-next-results-renderer {
    overflow-y: scroll;
    overflow-x: hidden;
    padding: 0;
    position: relative;
}

ytk-two-column-watch-next-results-renderer {
	--ytk-player-min-player-height: 600px;
	--ytk-watch-video-info-height: 142px;
	--ytk-player-outer-padding: 5px;
        padding-left: 15px;
        padding-right: 15px;
}


#anchors-row-content.ytk-masthead {
	display: flex;
	justify-content: center;
	left: 50%;
	position: var(--ytk-masthead-ui-mode-position);
	transform: translateX(-50%);
	margin-top: 0px;
}

.navigation-control-container.ytk-masthead {
	display: flex;
	height: 56px;
	justify-content: space-between;
	left: 0;
	min-width: 838px;
	position: fixed;
	width: 100%;
}

ytk-masthead {
	display: flex;
	flex-direction: column;
	height: 0;
	min-width: 838px;
	position: relative;
	top: 0;
	--ytk-masthead-two-row-with-text-height: 236px;
	--ytk-masthead-one-row-with-text-height: 164px;
	--ytk-masthead-two-row-without-text-height: 200px;
	--ytk-masthead-one-row-without-text-height: 128px;
	--ytk-masthead-height: var(--ytk-masthead-two-row-with-text-height);
	--ytk-masthead-ui-mode-position: relative;
	--ytk-masthead-avatar-url: url('https://www.gstatic.com/ytkids/web/watch_it_again/watch_it_again_avatar_1.png');
	--ytk-masthead-avatar-url-high-quality: url('https://www.gstatic.com/ytkids/web/watch_it_again/watch_it_again_avatar@2x_1.png');
	--ytk-masthead-center-content-default-min-width: 434px;
	--ytk-masthead-center-placeholder-content-default-min-width: 201px;
}

#top-row.ytk-masthead {
	background-color: transparent;
	display: flex;
	min-height: 32px;
	min-width: 640px;
	padding-top: 0;
	position: var(--ytk-masthead-ui-mode-position);
	z-index: 110;
}

#asset.ytk-kids-category-tab-renderer {
    height: 32px;
    width: 32px;
}

iron-icon.paper-icon-button {
    --iron-icon-width: 50%;
    --iron-icon-height: 50%;
}
  
}
