css:
@-moz-document url-prefix("https://lk-gnezdo.com/") {
body, div#filter form, table.account td, .pointer {
    background-color: #202020;
    color: #C0C0C0;
}
div#header_wrapper, li.title_cont, li.title_cont h3, li.title_cont a, #embedded, #embedded .right-corner, #embedded .left-corner, #logout i, a.arr_down, a.arr_up, tr.odd, tfoot td, .repeat, .print, .remove, .info, .popup-link, .popup i, div#stats ul.menu li.selected, div#stats ul.menu li.selected h3, div#stats ul.menu li.selected a, div#filter, div#footer_wrapper, div#header ul.menu li.selected, div#header ul.menu li.selected:hover, div#header ul.menu li.selected a, div#header ul.menu li.selected a:hover, div#header li.selected h4, div#header li.plus h4, tr.mdr-queue, div#main .simple tr.mdr-queue td {
    background: #202020;
}
#logo {
    display: none;
}
#multiauth {
    color: #7dee7d !important;
}
div#header ul.menu li.selected, div#header ul.menu li.selected:hover, div#header ul.menu li.selected a, div#header ul.menu li.selected a:hover, div#header li.selected h4, div#header li.plus h4 {
    color: white;
}
ul.item-3 a, ul.item-1 a, .simple a, #accountinfo a, #filter a, table.account a, div#aside a, div#main p a, div#pager li a, div#next a, div#prev a, td h4 a, .nav a, ul.item-4 a, ul.item-A a {
    color: #F0E68C !important;
}
div#header ul.menu a:hover, .activated a, div#login_form_cont, div#header.arc {
    background: url('https://lk-gnezdo.com/src/login.jpg');
}
a:hover {
    color: #F5F5DC !important;
}
#rest_of_creditors > a, #rest_of_creditors_rest > a, #rubr_list > span {
    color: #A9A9A9
}
tr.odd > td, select, input, td.submit_cont, div#main .simple tr.odd td, input[type='file'], #group_operations2 {
    background-color: #202020 !important;
}
#group_switcher, li.item-3.with-embedded a, li.with-embedded a, li.with-embedded-changed a {
    color: #C0C0C0 !important;
}
.simple a, #accountinfo a, #filter a, table.account a, div#aside a, div#main p a, div#pager li a, div#next a, div#prev a, td h4 a, .nav a {
    color: #FFD700;
}
div#next a.inactive, div#prev a.inactive, div#next a.inactive:hover, div#prev a.inactive:hover, fieldset.lg, input, select.disabled, input.submit.inactive, select, #apply_filter, input#img_url::placeholder, input[type='file'] {
    color: #DCDCDC;
}
div#num li.selected {
    background-color: #696969;
}
div#num > ul > li > a, div#prev > a {
    color: #778899;
}
textarea {
    background-color: #202020;
    color: rgba(28, 28, 28, 0.8);
}
#tags, #title, #small_text, a.order, #disable_after .selected a, #filter .selected a, #accountinfo a.credit_sum, #accountinfo a.credit_sum:hover, div#header #embedded .selected a:hover, div#num li.selected a, div#num li.selected a:hover, div#header li.selected a, div#header ul.menu li.selected a:hover, div#aside li.selected h3 a, div#aside li.selected a:hover, input.submit, input.changed, input.edit, table.account th, div#stats ul.menu li.selected a, div#stats ul.menu li.selected a:hover, a.inactive:hover, div#stats th, ul.menu a, div#logout a, div#footer a, td#set_rate a, .simple .r_names, .tabbed-dl dt li.selected a:hover {
    color: #F0E68C !important;
}
.result.expl, #image_message, #upload_message_image {
    color: #DC143C !important;
}
input#save_button {
    opacity: 0.8;
}
svg#chart > g > rect {
    fill: #696969;
}
svg#chart > g > text, svg#chart > g > circle {
    fill: #DCDCDC;
}
svg#chart > g > path {
    stroke: #DCDCDC;
}
svg#chart > g + g > text {
    fill: black;
}
div#header #embedded, div#header #embedded .selected, div#header ul.menu .selected, div#aside h3, input[type='submit'] {
    text-shadow: none;
}
.ac_over {
    background-color: #202020;
}
.positive, .success h3 {
    color: #3CB371;
}
.negative, span.required, .error h3 {
    color: #DC143C;
}
input {border: 1px solid #C0C0C0}
.teasers  tr {background-color: #202020 !important}
#gr_run_btn2 {color: white !important}
}
