css:
@-moz-document url-prefix("https://evgeny-nadymov.github.io/telegram-react/") {
.light .dialog-details {
    background-image: url(https://i.pinimg.com/originals/51/ed/c0/51edc046eb80046ee4755ee71d0f19ca.jpg);
    background-size: auto;
}
.dark .dialog-details {
    background-image: url(https://i.pinimg.com/originals/85/73/c8/8573c8c8db774b026441de6fe85e32d1.jpg);
    background-size: auto;
}
body,
.reply-content {
    font-size: 15px;
    line-height: 21px;
    font-family: Trebuchet MS;
}
.message.message-out:not(.message-bubble-hidden) .user-tile:not(.tile-poll)::before,
.message.message-out:not(.message-bubble-hidden) .chat-tile::before {
    display: none!important;
}
.message:not(.message-out):not(.message-bubble-hidden) .chat-tile::before,
.message:not(.message-out):not(.message-bubble-hidden) .user-tile::before {
    display: none!important;
}
.message-title,
.reply-content-title {
    font-size: 15px;
    font-weight: bold;
}
.message-bubble {
    border-radius: 5px 5px 5px 5px!important;
    padding: 10px 15px;
}
.dialog-details {
    background-color: #DFE4E8;
    width: 50%;
}
.dialog-active {
    border-radius: 0px;
    margin: unset;
    background: #6389A8;
    padding-left: 9px;
    padding-right: 9px;
}
.dialog {
    border-radius: 0px;
    margin: unset;
    padding-left: 9px;
    padding-right: 9px;
}
.day-meta-wrapper {
    line-height: normal;
    height: unset;
    padding: 5px 15px 7px 15px;
}
.reply {
    margin: 10px 0 10px;
}
.reply-border {
    height: 42px;
}
.message-bubble:not(.message-bubble-out) {
    margin-left: 10px;
}
.message-top:not(.message-out),
.message-middle:not(.message-out) {
    margin-left: 33px;
}
.message-bubble-out {
    margin-right: 10px;
}
.message-bubble {
    margin-left: 5px;
}
.message-out.message-top,
.message-out.message-middle {
    margin-right: 5px;
}
article {
    font-family: Roboto;
    font-size: 18px;
    line-height: 27px;
}
article h3,
article h4 {    
    line-height: 60px;
}
.photo,
.video,
.animation {
    margin-left: -15px;
}
.photo:not(.photo-title),
.video:not(.video-title),
.animation:not(.animation-title) {
    margin-top: -10px;
}
.photo:not(.photo-caption),
.video:not(.video-caption),
.animation:not(.animation-caption) {
    margin-bottom: -10px;
}
.photo.photo-title,
.video.video-title,
.animation.animation-title {
    margin-top: 10px;
}
.web-page-wrapper .photo
 {
    margin-top: 5px;
    margin-left: 0px;
    margin-bottom: 0px;     
}
}
