css:
@-moz-document domain('duckduckgo.com')
{
.nav-menu--slideout,
.tag-home__item,
.content-info,
.badge-link,
.logo-wrap--home,
.header-wrap--home,
.header--aside,
.header__logo-wrap,
.results--ads,
.results--sidebar,
.footer,
.foot-home,
.js-feedback-btn-wrap,
.module,
.feedback-btn
{
    display: none !important;
}

.site-wrapper--home,
.header-wrap--home,
.content-wrap--home,
.content--home,
.cw--c,
.search--home
{
    position: absolute !important;
    top: 0px !important;
    right: 0px !important;
    bottom: 5% !important;
    left: 0px !important;
    margin: auto !important;
}

.serp__results,
.header__search-wrap,
.zcm-wrap,
.result--more,
body,
.site-wrapper,
.is-not-mobile-device
{
    top: 0px !important;
    right: 0px !important;
    bottom: 0px !important;
    left: 0px !important;
    margin: auto !important;
    padding: 0px !important;
    min-width: 0% !important; 
}

.cw,
.results--main
{
    max-width: 100% !important;
}
}
