a.logo-text #logo{margin-top:0 !important}

#navbar1{background:#ddd}
#navbar1 .sidebar-background{display:none}
#navbar1 .nav > li > a{padding:6px 10px}

#titulo_principal, h4.titulo-principal{font-size:22px !important; text-align:center}

#f-invertir > div{text-align:center}

.sidebar-wrapper{font-size:12px}
.sidebar .user a, .bootstrap-navbar .user a{padding:2px 6px}
.sidebar .user, .bootstrap-navbar .user{padding:5px 0}
.sidebar .logo .logo-text, .bootstrap-navbar .logo .logo-text{padding:3px 0}

.sidebar, .main-panel{height:initial; max-height:initial}
.main-panel > .content{padding:12px 2px}

.container-fluid .row .card{margin-bottom:5px}

#div_detalle_proyecto #cerrar_detalle{margin:10px; opacity:.8; transform:scale(4)}
#div_detalle_proyecto #cerrar_detalle .indianred{color:#d20080 !important}
#div_detalle_proyecto #cerrar_detalle .indianred:hover{color:#00a3b4 !important}

#row_tabla_valores_activos .detalles-inversion .bd-b-color-base:first-of-type{display:none; border:5px solid red}

.detalles-inversion{border:1px solid #bbb; box-shadow:none}
.detalles-inversion .bd-b-color-base{border-color:#d0d0d0}
.detalles-inversion .bg_mercado{background-color:#f4f4f4 !important; height:100%}
.detalles-inversion .bg_mercado > .row.p-15.mrl-0{background:#fff; border:1px solid #ccc; border-radius:5px}
.detalles-inversion .col-md-12{padding-left:5px; padding-right:5px}
.detalles-inversion .row .pull-left{color:#666 !important; font-weight:bold}
.detalles-inversion .row .pull-right{color:#00a3b4}
.detalles-inversion .row .pull-right.rating-factura{color:#444}
.detalles-inversion .row.comprobador-espacio-financiado{border:none}
.detalles-inversion .inversiones-en-librado{padding-left:20px !important; padding-right:20px !important; padding-top:6px !important}
.funded{opacity:.6}
.funded .detalles-inversion{filter: grayscale(90%)}
.funded .pull-right.rating-factura{background:#333 !important; color:#fff !important}
.iinvest .detalles-inversion .bg_mercado{background:#fbecf5 !important}
.iinvest .inversiones-en-librado div{color:#d20080 !important}
.iinvest .detalles-inversion .row .pull-right{color:#d20080 !important}
.inoinvest .detalles-inversion .bg_mercado{background:#d4f0dc !important}
.inoinvesthurry .detalles-inversion .bg_mercado{background:#73dbaa !important}


.r07{background-color:#F90006 !important}
.r08{background-color:#F82000 !important}
.r09{background-color:#F74700 !important}
.r10{background-color:#F66D00 !important}
.r11{background-color:#F59300 !important}
.r12{background-color:#F4B900 !important}
.r13{background-color:#F3DE00 !important}
.r14{background-color:#E0F100 !important}
.r15{background-color:#BAF000 !important}
.r16{background-color:#93EF00 !important}
.r17{background-color:#6DEE00 !important}
.r18{background-color:#48ED00 !important}
.r19{background-color:#22EC00 !important}
.r20{background-color:#00EB01 !important}

#menu_principal .badge-mk2{font-size:14px !important; height:auto !important; line-height:16px !important}

.bootstrap-table table tbody tr td:first-child{padding-left:15px !important; text-align:left; width:400px}
#tabla_inversiones_formalizadas thead th:nth-child(8), #tabla_inversiones_formalizadas tbody td:nth-child(8){display:none}
.bootstrap-table .table, .bootstrap-table .table > tbody > tr > th, .bootstrap-table .table > tfoot > tr > th, .bootstrap-table .table > thead > tr > td, .bootstrap-table .table > tbody > tr > td, .bootstrap-table .table > tfoot > tr > td{padding:0 !important}
.bootstrap-table table .icon_info{display:none}
.bootstrap-table table .etiqueta-a1-big{font-size:22px !important}
.bootstrap-table table .etiqueta-a1-medium{color:#999}

.bootstrap-table th[data-field="RUTA_LOGO"]{border:3px solid red} 



#resumen-dashboard ul li{text-align:center; width:12.5% !important}
#resumen-dashboard ul li > div{border-color:#93D6DD; min-height:6em !important; padding:5px}

@media (min-width: 1200px) {
    #navbar1{left:0; position:fixed; top:0} 
}
