/* ==UserStyle==
@name           FakeMail.net Dark Mode
@namespace      USO Archive
@author         felipesantos989
@description    `desc.`
@version        20210729.3.3
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("fakemail.net") {
body {
    color: #dedede;
    background-color: #252528;
    font-family: 'Libre Franklin', sans-serif;
}

/* copyright bar */
.navbar-default {
    display: none;
    background-color: #252528;
    border-color: #39393e;
}

.main .banner {
    background: #252528;
    border-left: 1px solid #39393e;
}

.main .navigace ul li a {
    background-color: #313135;
    border-bottom: 1px solid #39393e;
}

a, a:visited, a:link, a:active {
    color: #dedede;
}

.info {
    background-color: #fbd0;
}

.info div .links p span {
    color: #dedede;
}

.info div div:not(.links):not(.avatarDiv) {
    background-color: #313135;
    border: 1px solid #39393e;
}

.main .navigace ul li.active a {
    color: #dedede;
    background: #313135;
}

.table-hover>tbody>tr:hover {
    background-color: #2f2f2f;
}

.table>thead>tr>th {
    vertical-align: bottom;
    border-bottom: 2px solid #353535;
}

.main .dynamic {
    border: 1px solid #424242;
    flex-grow: 1;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    border: 1px solid #313135;
}

/* logo */
.col-md-2 {
    display: none;
}

.donate {
    display: none;
}

.nav-tabs > li > a:hover {
    border-color: rgba(238, 238, 238, 0) rgba(238, 238, 238, 0) rgba(221, 221, 221, 0);
}

.borderBottom {
    border-bottom: 2px solid #424242;
    background-color: #2f2f2f;
    height: 55px;
    border-top: 1px solid #424242;
}

.otevrenLogo {
    display: none;
}

/* change email */

.modal-content {
    background-color: #2f2f2f;
}

.text-muted {
    color: #dedede;
}

.stinMensi {
    display: none;
}

.site-title h1 {
    font-family: sans-serif;
    visibility:hidden;
}

.table {
    background-color: #252528;
    margin: 0px;
}

.row.info div.tbg div.links {
    background-color: #252528;
    border: 1px solid #3a3a3a;
}

.row.info div.tbg div.links a {
    background-color: #252528;
    color: #fff;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    border-top: 1px solid #444;
}

.row.info div.tbg div:not(.links).addHere {
    background-color: #2f2f2f;
    display: none;
}

.row.info div.tbg div:not(.links):not(.addHere) #odpocetMobil {
    background: #313135;
    border: 1px solid #484848;
}
    
.wpcc-color-custom-1780161860.wpcc-container {
    display: none;
}
    
input {
    background-color: #242424;
    border-width: 1px;
    border-color: #363636;
    outline: none;
}

.modal-footer {
    border-top: none;
}

.modal-header {
    border-bottom: none;
}

}
