/* Left nav row. */
#left-navigation-container li {
    padding-left: 5px;
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
}

#left-navigation-container li.selected {
    background-color: #fdfdfd;
}

/* Container for icon and text. */
#left-navigation-container a {
    text-decoration: none;
    color: #fbfbfb;

    width: 100%;
}

#left-navigation-container li.selected a {
    color: #000;
    font-weight: bold;
}

#left-navigation-container a:hover {}

/* Left navigation text. */
#left-navigation-container span {
    display: inline-block;
    vertical-align: top;
    margin-top: 10px;
    margin-left: 5px;
    font-size: 1.0em;
    font-weight: bold;
}

#left-navigation-container li.selected span {
    font-weight: bold;
}

/* Left navigation icon. */
#left-navigation-container label {
    display: inline-block;
}


/* right nav stuff */
#master-layout-right-container {
    position: absolute;
    right: 0px;
    height: 100%;
    z-index: 2000;
    width: 250px;
    border-style: solid;
