@-moz-document url("https://typing-tube.net/") {
/*トップページ*/

/*ヘッダーとコンテンツの間(デフォルトは102px)*/
 .content {
      padding-top:0px!important;
}
/* 消したもの */
 img.w-100[src*="/images/main.gif"]
/* トップページの画像 */
 {
      display:none!important;
}
#google_image_div, .adsbygoogle, .img_ad, footer {
      display: none !important;
}
