@-moz-document domain("timetable.my.uq.edu.au") {
    /*[[theme]]*/
    
    #outer-header {
        min-width: 100%;
    }
    
    .aplus-container .top-bar {
        background-color: var(--bg-col);
        color: var(--text-col);
        border: 0;
        box-shadow: 0px 5px 5px 2px rgba(0, 0, 0, 0.4);
    }
    
    #outer-container > div > div.top-bar > ul > li > a:hover {
        background-color: var(--accent-col);
    }
    
    #outer-container > div > div.top-bar > ul > li > a {
        color: var(--text-col);
    }
    
    #outer-container {
        display: flex;
        width: 100%;
        height: 100%;
    }
    
    #mobile-left-menu,
    #mobile-left-menu > li,
    #mobile-left-menu > li > a {
        background-color: var(--bg-light) !important;
        border: 0 !important;
        color: white !important;
    }
    
    .site-header {
        background-color: var(--accent-col) !important;
    }
    
    #outer-footer > table > tbody > tr > td {
        background-color: var(--accent-col);
    }
    
    /* Main content */
    .aplus-container {
        width: 100%;
        margin: 0;
        position: static;
    }
    
    .left-col,
    .right-col {
        background-color: var(--bg-light);
    }
    
    .profile-summary {
        border: 0 !important;
    }
    
    /* Header above each cell (e.g. Enrolment, Home, etc)*/
    .aplus-container .module .upper_sec {
        border: 0;
        background-color: var(--bg-dark);
        color: var(--text-col);
    }
    
    h3 {
        color: var(--text-col) !important;
    }
    
    .subject-list > li {
        background-color: var(--bg-light) !important;
        border: none !important;
    }
    
    .arrow-container {
        color: var(--accent-col) !important;
    }
    
    a > small {
        color: var(--text-col) !important;
    }
    
    #search_box,
    #url-input {
        color: var(--text-col) !important;
        background-color:var(--bg-lighter) !important;
        border:0;
    }
    
    div.my_event_body {
        color: black !important;
    }
    
    #timetable-grid,
    #timetable-grid > div.dhx_cal_header > div,
    .dhx_cal_date,
    .dhx_cal_header,
    .dhx_scale_hour {
        background-color: var(--bg-col) !important;
        color: var(--text-col) !important;
        border: 0;
    }
    
    .dhx_scale_holder {
        background-image: none !important;
        border-color: black;
    }
    
    #timetable-grid > div.dhx_cal_data.dhx_resize_denied.dhx_move_denied {
        border: 1px solid black;
    }
    
    #timetable-tpl > div.upper_sec.upper_button_sec > div.slider-title.desktop-only {
        color: var(--text-col);
    }
    
    .dhx_cal_prev_button,
    .dhx_cal_next_button {
        color: var(--text-col) !important;
        background-color: var(--accent-col);
        box-shadow: 0px 5px 5px 2px rgba(0, 0, 0, 0.4);
        border: 0px;
        border-radius: 10px;
        transition: all 0.1s ease;
        transform: scale(1);
    }
    
    .dhx_cal_prev_button:hover,
    .dhx_cal_next_button:hover {
        color: var(--text-col) !important;
        background-color: var(--accent-col);
        box-shadow: 0px 5px 5px 2px rgba(0, 0, 0, 0.4);
        border: 0px;
        border-radius: 10px;
        transition: all 0.1s ease;
        transform: scale(1.1);
    }
    
    /* Preferences page */
    .aplus-table {
        width: 100%;
    }
    
    .aplus-container table.aplus-table thead,
    #preferences-tpl > div.lower_sec > div > table > thead > tr > th {
        background-color: var(--bg-light);
        color: var(--text-col);
        border: 0;
    }
    
    #preferences-tpl > div.lower_sec > div > table > thead > tr:nth-child(1) > th {
        background-color: var(--bg-col) !important;
        border: 0;
    }
    
    #preferences-tpl > div.lower_sec > div:nth-child(1) > table > tbody > tr > td {
        padding: 5px 5px 5px 5px !important;
        border: 0 !important;
    }
    
    #preferences-tpl > div.lower_sec > div:nth-child(1) > table > tbody > tr:nth-child(even) {
        background: var(--bg-col);
    }
    
    /* Session timeout page */
    #outer-container > div > div.col {
        background-color: var(--bg-col);
    }
    
    #outer-footer {
        max-width: none !important;
    }
    
    #preferences-tpl > div.lower_sec > div:nth-child(1) > table > tbody > tr:nth-child(odd) {
        background: var(--bg-light);
    }
    
    .msg-head {
        color: var(--bg-col);
    }
    
    .lower_sec > a {
        color: var(--accent-col) !important;
    }
    
    #outer-container > div > div.col > div > div > div.lower_sec {
        color: var(--text-col);
    }
  }

