css:
@-moz-document url-prefix("https://weibo.com/u/") {
/*微博个人主页*/


.S_page .WB_miniblog,.WB_miniblog{
    background: #f8f8f8;
	
}
.WB_frame{
	margin-top: -2px!important;
	padding-top: 0px !important;
	border-radius: 5px!important;
}

body, .S_page {
    background-color: #f8f8f8;
}


.placeholder{
	margin-top: 2px;
}
.WB_cardwrap{
    margin: 0 0 10px 0!important;
    box-shadow: 0 0 6px 0.3px rgba(0, 0, 0, 0.08);
    border-radius: 5px;
	transition: .3s;
}
.WB_tab_a{
	border: none!important;
    box-shadow: 0 0 6px 0.3px rgba(0, 0, 0, 0.08)!important;
}
.PCD_tab{
	box-shadow: 0 0 6px 0.3px rgba(0, 0, 0, 0.08);
	transition: .3s;
}
.WB_cardwrap:hover{
    box-shadow: 0 0 6px 0.3px rgba(0, 0, 0, .15);
}
.WB_feed_v3 .WB_media_a_mn li{
	border-radius: 3px;
}
.PCD_tab.S_bg2{
	border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}
.S_bg1, .SW_fun_bg:hover, .SW_fun_bg_active {
    background-color: #f2f2f5;
}
#Pl_Official_Headerv6__1{
	background-image: linear-gradient(#f8f8f8, #c1c1c1);
}
.WB_feed_detail.clearfix{
	background-image: none!important;
}
.WB_global_nav .gn_nav_list li a .S_txt1 {
    font-size: 0.95em;
}

.m_wrap.W_tog_hover{
	border-radius: 5px;
}
}

@-moz-document domain("weibo.com") {
/*全局*/
/*状态栏*/
.WB_global_nav .gn_search_v2 {
    height: 30px;
    margin: 8px 0 0 10px;
    background-color: #f2f2f5b8;
    border: 1px solid #cccccccf;
    border-radius: 4px;
}
.WB_global_nav {
   	border-top: none;
    box-shadow: -1px -1px 4px 1px rgba(0, 0, 0, 0.3);
}
/*向上按钮*/
#base_scrollToTop{
    border-radius: 5px;
 	margin-left: 470px;
	box-shadow: 0 0 6px 0.3px rgba(0, 0, 0, 0.08)!important;
	max-height: 35px;
}
/*删除东西*/
.pf_use_num,.pf_copy_icon,.gn_nav_list>li:nth-child(2),.gn_nav_list>li:nth-child(3),.gn_nav_list>li:nth-child(4),.W_new_count,.mini.clearfix,
.webim_fold.webim_fold_v2.clearfix,#Pl_Official_LikeMerge__15,#Pl_Core_RecommendFeed__18,#Pl_Core_UserGrid__8,#Pl_Core_RecommendFeed__19
{
	display: none !important;
}
}

@-moz-document url-prefix("https://s.weibo.com/") {
/*搜索页面*/
.card.card-user-b.s-pg16.s-brt1{
	border-radius: 5px!important;
	background: #fff;
	margin-bottom: 10px;
}
.card-wrap {
    border: none;
    background: none;
}
.card.card-user-b.s-pg16.s-brt1,.m-filtertab,.m-sub-nav,.m-main-nav.s-mt28
{
	box-shadow: 0 0 6px 0.3px rgba(0, 0, 0, 0.08);
	margin-left: 3px!important;
	transition: .3s;
}
.card.card-user-b:hover.s-pg16.s-brt1:hover,.m-filtertab:hover,.m-sub-nav:hover,.m-main-nav.s-mt28:hover
{
	box-shadow: 0 0 6px 0.3px rgba(0, 0, 0, .15);
}
.m-search .searchbox input {
    font-size: 17px;
    border-radius: 5px;
}
}
