/**
    GitHub - Clean Light
    Copyright 2020 Riski Web ID.
    ____________________________
    Version : 1.1
    Description : A Simple Clean and Light Theme for GitHub
    Repo : https://github.com/riskiwebid/github-clean-light
    Author : Riski Web ID
    License : MIT
*/

@-moz-document url-prefix("https://github"), domain("github.com") {

    /* variables */
    :root {
        --primary-color: #0076ec;
        --green: #00bf67;
        --text-color  : #333;
        --gray : #808080;
        --gray-light: #ececec;
        --bg-default : #f9f9f9;
        --light: #fff;
        --dark : #1f2127;
        --darkness : #131419;
    }

    /* body */
    body {
        background-color: var(--bg-default) !important;
    }

    /* application main */
    .hx_reponav .reponav-item {
        padding: 4px 12px 4px!important;
        font-weight: bold;
        color: var(--gray);
    }
    .reponav-item.selected {
        border-color: var(--primary-color) transparent;
        border-bottom: 0 !important;
        color: #444;
    }
    .repohead h1 {
        font-size: 20px;
        font-weight: bold;
    }
    .reponav-item .octicon {
        margin-bottom: 2px;
    }
    .reponav-item.selected .octicon {
        fill: var(--primary-color);
    }
    .overall-summary {
        border: none;
        border-bottom: none !important;
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        border-radius: .1rem !important;
        background-color: var(--light);
    }
    .repository-content .overall-summary-bottomless {
        border: none;
        border-bottom: none !important;
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        border-radius: .1rem !important;
        background-color: var(--light);
    }
    .repository-lang-stats-graph .language-color:first-child {
        border-bottom-left-radius: .1rem;
    }
    .repository-lang-stats-graph .language-color:last-child {
        border-bottom-right-radius: .1rem;
    }
    .commit-tease {
        background-color: #cae5ff;
        border: 1px solid #c8e1ff;
        padding: 12px 30px;
    }
    table.files {
        border-radius: .1rem;
    }
    table.files td.icon {
        padding-left: 30px;
    }
    table.files td.age {
        padding-right: 30px;
    }
    .Box {
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        border-radius: .1rem !important;
        background-color: var(--light);
        border: none !important;
    }

    .Box-body {
        background-color: var(--light);
        border: none !important;
        margin-left: -1px;
        margin-right: -1px;
    }

    .repository-content .Box .Box-body.blob-wrapper.data, .blob-code-inner {
        font-weight: 500 !important;
        line-height: 22px;
    }

    /* first table row blob-num and blob-code */
    tr:first-child td[id^="L"] {
        padding-top: 22px;
    }

    /* last table row blob-num and blob-code */
    tr:last-child td[id^="L"] {
        padding-bottom: 22px;
    }

    /* button */
    .btn {
        color: #24292e;
        background-image: none;
        border: none;
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        border-radius: .1rem !important;
        background-color: var(--light);
    }
    .btn.hover,
    .btn:hover {
        background-color: var(--primary-color);
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        color: var(--light);
        background-image: none;
    }
    .btn.selected, .btn:active, .btn[aria-selected=true], [open]>.btn {
        background-color: var(--primary-color);
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        color: var(--light);
    }
    .social-count {
        color: #24292e;
        border: none;
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        border-radius: .1rem !important;
        background-color: var(--light);
    }
    .get-repo-select-menu .btn.btn-primary {
        background-color: var(--green) !important;
        color: #fff;
    }

    /* header */
    .position-relative.js-header-wrapper {
        background-color: var(light-gray);
    }
    header.Header {
        background-color: var(--primary-color);
        margin: auto;
        padding-left: 186px;
        padding-right: 186px;
    }
    .Header .Header-item .Header-link .avatar {
        border-radius: 30px;
        width: 28px;
        height: 28px;
    }

    .pagehead {
        border-bottom: none !important;
        box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.1);
        border-radius: .1rem !important;
    }
        
    .notification-indicator .mail-status {
        position: absolute;
        top: -6px;
        left: 6px;
        z-index: 2;
        display: none;
        width: 14px;
        height: 14px;
        color: #fff;
        background-image: linear-gradient(#e8a000,#ffd095);
        background-clip: padding-box;
        border: none;
        border-radius: 50%;
    }

    /* etc */
    .bg-gray-light {
        background-color: var(--gray-light);
    }

}
