css:
@-moz-document domain("jwt.io")
{
	body {
		background: #222;
	}

	#libraries-io {
		background: #333 !important;
	}

	.libraries-jwt article .panel-body {
		background: #222 !important;
		color: #888;
	}

	.libraries-jwt article .author-info {
		background: #444 !important;
		color: #fff !important;
	}

	.libraries-jwt article .author-info a {
		color: #bbb !important;
	}
	.container h1 {
		color: #888 !important
	}
	.tab-link a,
	.jwt-explained p,
	.algorithm {
		color: #888 !important
	}

	.libraries-jwt article .panel-body p code {
		background: #373030 !important;
		color: #dd3434 !important;
		padding: 1px 5px;
	}

	.jwt-select {
		color: #000 !important;
	}

	.jwt-select option {
		background: #333 !important;
		color: #fff !important;
	}

	div.warning {
		background: #333;
		border: 1px solid RGBA(255, 192, 0, .5);
		color: RGBA(255, 192, 0, .5);
	}

	.CodeMirror,
	.jwt-playground .output .jwt-signature .text-line a,
	.jwt-playground .output .jwt-signature pre {
		background: #333;
	}

	.CodeMirror .cm-jwt-dot {
		color: #fff !important;
	}

	.jwt-playground .algorithm-code .tab-content .jwt-explained .pre-encode input,
	.jwt-playground .algorithm-code .tab-content .jwt-explained .pre-encode textarea,
	pre.RS256 textarea {
		background: #88868655;
		border: 1px solid #222;
		padding: 0 5px;
		color: #a6a9aa;
	}

	.jwt-playground .validation-status {
		background: transparent;
		transition: all 1s;
	}

	.jwt-playground .validation-status.valid-token {
		color: #25b725;
	}

	.jwt-playground .debugger-footer .website-share button,
	.sources .tshirt a {
		background: transparent;
	}

	.jwt-playground .debugger-footer .website-share button,
	a.buy {
		background: transparent;
		border: 1px solid #00b9f1;
	}

	.jwt-playground .debugger-footer .website-share button:hover,
	a.buy:hover {
		background: rgba(0, 0, 0, .34);
	}
}
