css:
@-moz-document domain("calmlywriter.com") {

@media only screen and (max-width: 980px) {
    .droid {
		font-size: 20px;
    }
}



/* Background */

div#wrapper {
    background-color: #55446b;
}
#calmlylogo img { /* Logo */
    mix-blend-mode: multiply;
}
#calmlylogo { /* Logo Div */
    opacity: 1 !important;
}
#wrapper.disablescroll + #calmlylogo { /* Logo Div */
    opacity: 0 !important;
}


/* Text */

div#wrapper {
    color: #87e8c0;
}
.focusmodeon #blockfocus,
.focusmodeon #blockfocus * { /* Focus Mode On */
    color: #87e8c0 !important;
}
.focusmodeon * { /* Focus Mode On, Out of Focus */
    color: rgba(135, 232, 192, .3);
}
#main a,
#main a:visited,
#main a:active,
a.oklink { /* Links */
    color: #87e8c0;
    text-decoration: underline;
    background-image: none;
    text-shadow: none;
}
#main ul li:before,
#main ol li:before { /* UL Bullets and OL Numbers */
    color: #87e8c0;
}


/* Scrollbar */

::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-thumb {
    background-color: #87e8c0 !important;
    border-width: 0 !important;
    border-radius: 5px;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #6abd9b !important;
}




















}
