css:
@-moz-document domain("my.claflin.edu") {
.portlet, .default-border-alternate-one, .default-top-border-alternate-one, .default-bottom-border-alternate-one {
	border-color: #333 !important;
}

a, .body-text, .loc, .prgName, .top-nav-bar .nav-container .main-nav-submenu-container .user-login.popover .message {
	color: silver !important;
}

a, .body-text {
	text-decoration: none !important;
}

a:hover {
	text-decoration: underline !important;
}

.sidebar-link-title, .wysiwygtext span em, .degOff {
	font-style: normal !important;
}

#portlet-grid span a {
	text-decoration: underline !important;
}

.groupedGrid td {
	color: black !important;
}

.groupedGrid tbody, .groupedGrid tbody .alt {
    border-bottom: solid 1px #666 !important;
    background: #333 !important;
}

.alert-container.feedback, .card-layout .card-set-wrapper {
	border-color: #999 !important;
}

.hint {
    border: 1px solid #111 !important;
}

.card-layout .masonry .card, .alert-container.feedback, .ccSection table, .contentTabs li.selected, .slide-menu-right .navbar li #pagePortlets a, .wysiwygtext p, .slide-menu-right .navbar li #myPages a, .portlet-header-bar, .Timecard .tcMainDisplay, .Timecard .tcMainDisplay .tcMainHeader, .tcHoursEntry, .CDPeriodClosed, .tdUnd, .top-nav-bar .nav-container .main-nav-submenu-container .popover, .tdCap, .tdCapN, .tdCapP, .tdVal, .tdValN, .tdValP, .title, .secondary-alternate-background-one, .mce-panel, select, .hdr, .row .card-description .wysiwygtext span, .portlet.pageOptions, .portlet.pageLayouts, .portlet.page-access, .settings-wrapper .inner-settings-wrapper .modalHeading, .settings-wrapper .portletSelection, .preferencesPanel .portletSelection, .fc-event-container a {
	background: #333 !important;
}

.groupedGrid thead td, .groupedGrid thead th, .groupedGrid tbody.gbody tr th {
	background: #555 !important;
}

body, .top-nav-bar .nav-container .link-scroll .h-menu a, .slide-menu-right .navbar li button, .CDInactiveDay, .CDPeriodClosed, .ex-crm-portlet-view table:not([cellspacing="0"]) td, .ex-crm-portlet-view table:not([cellspacing="0"]) th, .degOff, .mce-label, .groupedGrid tbody td, .card-layout .masonry .card .sub-info, .calendar-portlet, .alert-container.feedback, input, .sp4, .st3, .moreOptionsPanel .optionsHeader, .card-description p span, select, .portlet-padding table td, .row .card-description .wysiwygtext span, .top-nav-bar .nav-container .main-nav-submenu-container .user-login.popover .user-name {
	color: #f3f7f7 !important;
}

body, .buttonBar, .top-nav-bar .nav-container .link-scroll .h-menu a:hover, .slide-menu-right, .slide-menu-right .navbar, #portlet-grid span a span, input, .CDInactiveDay, .Timecard .tcMainDisplay .CDMainTable, #wrap, .Timecard .tcMainDisplay .CDMainTable .CDLegendTable, .wysiwygtext p, .ccSection p span a, .hint, .pToolbar, #masthead h1, #masthead, .tblFacList, .tblCrsList, .portlet-padding table td, .default-bottom-border-alternate-one, .default-border-alternate-one, .tblDetails, #divDetails, .CS .GrayBordered, .page-title-btn, .portlet.pageSettings, .preferencesPanel .inner-settings-wrapper .modalHeading, .groupedGrid tbody th, .wysiwygtext p *, .wysiwygtext div, td a, td a:link, td a:visited, font table {
	background-image: url(data:image/gif;base64,R0lGODlhBAAEAKECABERESQkJP///////yH5BAEKAAIALAAAAAAEAAQAAAIFhB6nhlIAOw==) !important;
    background-repeat: repeat !important;
    background-color: #181818 !important;
}

.top-nav-bar, .popover-content, footer {
	background: black !important;
}

.portlet-header-bar h3, .main-crumbs, .left font, .wysiwygtext span strong, .sidebar-link-title, .Timecard .tcTotalsNote, .tcOverriddenHours, .hint font, .sp3, .wysiwygtext span em, #divDetails h5 span, .wysiwygtext strong span, .PortletError, h2 i {
	color: #FFA500 !important;
}

#portlet-grid span a span, .menu-links a, .slide-menu-right .navbar li #myPages a, .ExpandablePanel, #included-calendars-link, .link-btn, .primary {
	color: #7dd8ff !important;
}

.btn-primary {
	background: #337ab7;
}

.slide-menu-right .navbar .quick-links {
	border-top: 12px solid #181818;
}

.tblFacList, .tblCrsList, .footer-wrapper, .card-layout .masonry .card .btn-primary, .form-control, .ex-crm-portlet-view table:not([cellspacing="0"]) td, .fc-event-container a {
	border: none !important;
}

.mce-label {
	text-shadow: none;
}
}
