css:
@-moz-document url-prefix("https://www.ettv.to/"), url-prefix("https://www.ettvdl.com/"), url-prefix("https://www.ettvcentral.com/") {
/* Dark theme for ETTV
2020-02-13: creation
2020-04-02: added new ETTV domain
2020-09-06: added new ETTV domain + forum style


/* Main Page */
body {
    color: rgb(200, 200, 200);
    background-color: rgb(38, 38, 38);
}

/* Menu in red bar */
.menu ul li a {
    color: rgb(200, 200, 200);
}

/* Wrapper around large search box */
.search_wrap_home {
    border: 1px solid rgb(128, 128, 128);
    background-color: rgb(38, 38, 38);
}

/* Small search box in red menu bar */
form.search_form input[type="text"] {
    border: 1px solid rgb(128, 128, 128);
    background: rgb(98, 98, 98);
    color: rgb(250, 250, 250);
}
i.fa {
    font-size: 16pt;
    color: rgb(55, 55, 55);
}

/* Large search box */
div.search_home input[type="text"] {
    border: 1px solid rgb(128, 128, 128);
    background: rgb(98, 98, 98);
    color: rgb(250, 250, 250);
}


/* Home Page */
/* Search Cloud list */
.search_cloud {
    background-color: rgb(98, 98, 98);
    border-bottom: 2px solid rgb(55, 55, 55);
    color: #000
}
.search_cloud a {
    color: rgb(28, 28, 28) !important
}

/* ETTV Recommended */
.myBlock-caption {
    color: rgb(200, 200, 200);
    border: 1px solid rgb(200, 200, 200);
}
.myBlock {
    border: 1px solid rgb(200, 200, 200);
}

/* Most Downloaded and below */
.myFrame-caption {
    border: 1px solid rgb(200, 200, 200);
}
h2.caption-h2 {
    color: rgb(200, 200, 200);
}
.subcaption a {
    /* RSS */
    color: rgb(200, 200, 200);
}
.table-bordered {
    border: 1px solid rgb(55, 55, 55);
}
.table-bordered > tbody > tr > td,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > td,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > thead > tr > th {
    border: 1px solid rgb(55, 55, 55);
}
.myFrame-foot {
    display: none;
}

/* Search results */
input[type="text"] {
    border: 1px solid rgb(55, 55, 55);
    background: rgb(98, 98, 98);
    color: rgb(250, 250, 250);
}
input,
select {
    border: 1px solid rgb(55, 55, 55);
    background: rgb(98, 98, 98);
    color: rgb(250, 250, 250);
}
.myFrame-caption {
    color: rgb(200, 200, 200);
}

/* Torrent page */
#downloadbox {
    background: rgb(38, 38, 38);
    box-shadow: inset 0 0 0 1px rgb(55, 55, 55);
}
.download legend {
    color: rgb(200, 200, 200);
    background: rgb(55, 55, 55);
}
.table_head {
    color: rgb(200, 200, 200);
    background: rgb(55, 55, 55);
}
table.table.table-hover.table-bordered tr:hover,
.table_table tr:hover {
    background-color: rgb(30, 30, 30) !important
}
table td,
table th {
    background-color: transparent !important;
    background-image: none !important;
}

/* Login page */
.form-control {
    background-color: rgb(98, 98, 98);
}
input[type="password"] {
    color: #a30000;
}

/* Signup page */
.form-control::placeholder {
    color: rgb(250, 250, 250);
}
.form-control {
    color: rgb(250, 250, 250);
}

/* Forum */
table td, table th {
	background-color: #2b2b2b !important;
	background-image: none !important;
}

/* Images in torrent list */
/* Comments */
table tr th:nth-child(5) img {
    padding-left: 20px !important;
    padding-top: 20px !important;
    background: url('data:image/png;base64,') no-repeat !important;
}
/* Seed */
table tr th:nth-child(6) img {
    padding-left: 20px !important;
    padding-top: 20px !important;
    background: url('data:image/png;base64,') no-repeat !important;
}
/* Leech */
table tr th:nth-child(7) img {
    padding-left: 20px !important;
    padding-top: 20px !important;
    background: url('data:image/png;base64,') no-repeat !important;
}
}
