css:
/* i really want this to be global */

table#leadTable {
    width: 100% !important;
}

textarea.vvsu-ui-padding {
    width: 100% !important;
    text-align: left;
}

#leadTable input {
    min-width: 700px;
}

input#but_ {
    padding: 14px;
    color: #fff;
    background-color: #64d863;
    border: none;
    border-radius: 5px;
    font-size: 0.9rem;
    text-shadow: -2px 3px 9px rgba(0, 0, 0, 0.15);
    margin-right: 9px;
    width: 85%;
}

input#but_ + input {
    width: 14% !important;
    min-width: 14%;
    padding: 14px;
    color: #464646;
    background-color: #eceef0;
    border: none;
    border-radius: 5px;
    font-size: 0.9rem;
}

.CodeMirror-code {
    text-align: left !important;
}

.CodeMirror.cm-s-monokai.CodeMirror-wrap {
    min-height: 520px;
}
