css:
@-moz-document regexp(".*\.console\.aws\.amazon\.com/.*"){

:root {
    --awsui-color-background-container-content: #000;
    --awsui-color-text-header-default: #DDD;
    --awsui-color-background-button-normal-default: rgba(255, 255, 255, .1);
    --awsui-color-border-button-normal-default: rgba(255, 255, 255, .2);
    --awsui-color-background-container-header: rgba(255, 255, 255, .1);
    --awsui-color-text-base: white;
}
body, html {
    background: #000;
    color: white;
}
a, a span {
    color:rgb(50, 176, 255) !important
}
div[role="navigation"] div, .awsui .awsui-select-dropdown-option {
    border-color:#444 !important;
}
body > div > div, .awsui-util-containeritemCard toggle-collapsed, #search-box-input-dropdown ul li:hover, .awsui, .awsui .awsui-app-layout, .awsui .awsui-app-layout, .awsui .awsui-app-layout__content {
    background: transparent !important;
    background-color: transparent !important;
    color: white;
}
.awsui-form-field-description, .awsui .awsui-input, div[awsui-expandable-section-region="header"], .home-content .itemCard .item-title li, .awsui-select-option-description {
    color: #ddd !important;
}
h1.home-banner, .awsui .awsui-input::-webkit-input-placeholder {
    color: white !important;
}
.awsui-util-container-header, .awsui-util-container, .awsui .awsui-select-dropdown-option, awsui-app-layout, awsui-app-layout .awsui-app-layout {
    background: rgba(255, 255, 255, .1) !important;
    color: white;
    border-color:#444 !important;
}
#search-box-input-dropdown, #search-box-input-dropdown ul {
    background:#222;
}
.awsui.awsui-select-dropdown-option:hover {
    background: #222 !important;
}
#search-box-input-dropdown, #search-box-input-dropdown div {
    background: transparent;
    color: white;
    border-color:#666 !important;
}
.awsui-autosuggest-input-wrapper.awsui-dropdown-trigger input {
    background:rgba(255, 255, 255, .2) !important;
    border-color: rgba(255, 255, 255, .2) !important;
}
.service-group-icon {
    filter: brightness(100);
}
svg g > path, svg > path {
    stroke: rgb(147, 201, 255) !important;
}
svg > path {
    fill: rgb(61, 86, 111) !important;
}
.home-content .itemCard .item-title, .item.home-row-item.solution-container > li a {
    background: transparent !important;
    color: #ddd;
}
.home-content, .home-content #aws-services #awsgnav, .sidebar-section-item, .toggle-action, .itemCard div:last-child.awsui-util-container-footer {
    border-color: rgba(255, 255, 255, .2) !important;
    border-top-color: rgba(255, 255, 255, .2) !important;
    border-bottom-color: rgba(255, 255, 255, .2) !important;
}
.Panel {
    border-color: var(--awsui-color-background-button-normal-default);
}
.awsui a:hover {
    background: var(--awsui-color-background-button-normal-default);
}
.awsui-input.awsui-input-type-text {
    background: var(--awsui-color-border-button-normal-default);
    border-color: var(--awsui-color-border-button-normal-default);
}
.awsui .awsui-alert-type-info {
    background: #222;
}}
