css:
@-moz-document domain('timezone.io') {

body, .site-branding, .site-footer, .time-control-now, .hp-section.alt {
	background-color: black;
	color: white;
}

input, textarea, .autocomplete--option {
	color: black;
}

.avatar-placeholder {
	background-color: black;
	border: 1px solid white;
}

.material-icons {
	color: white;
}

.header-avatar::after {
	border-top-color: white !important;
}

a::after, a:visited::after {
	background-color: grey;
}

.person-info, .user-menu {
	background-color: #222;
}

a, a:visited {
	color: grey;
}

/**
* Fixes Time Selector Bar
*/
input[type=range] {
  -webkit-appearance: none;
  margin: 18px 0;
  width: 100%;
}
input[type=range]:focus {
  outline: none;
}
input[type=range]::-webkit-slider-runnable-track {
  width: 100%;
  height: 8.4px;
  cursor: pointer;
  animate: 0.2s;
  box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;
  background: grey;
  border-radius: 1.3px;
  border: 0.2px solid #010101;
}
input[type=range]::-webkit-slider-thumb {
  box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;
  border: 1px solid #000000;
  height: 36px;
  width: 8px;
  border-radius: 3px;
  background: #ffffff;
  cursor: pointer;
  -webkit-appearance: none;
  margin-top: -14px;
}
input[type=range]:focus::-webkit-slider-runnable-track {
  background: #367ebd;
}
input[type=range]::-moz-range-track {
  width: 100%;
  height: 8.4px;
  cursor: pointer;
  animate: 0.2s;
  box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;
  background: grey;
  border-radius: 1.3px;
  border: 0.2px solid #010101;
}
input[type=range]::-moz-range-thumb {
  box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;
  border: 1px solid #000000;
  height: 36px;
  width: 8px;
  border-radius: 3px;
  background: #ffffff;
  cursor: pointer;
}
  
}
