css:
@-moz-document domain("pokedextracker.com") {
body {
    background-color: #262626;
}

.dex-search-bar {
    background-color: #333333;
    border-bottom: 1px solid #2D2D2D;
}

.main-footer {
    background-color: #333333;
}

/* Alert text box */
.alert.alert-muted {
    color: #DDDDDD;
    border: 2px solid #DDDDDD;
}

.alert.alert-muted a {
    color: #BBD5EE;
}

.alert.alert-muted a:hover {
    color: #6290BD;
}

/* Dex's Title */
header h1 {
    color: #8CB3D9;
}

h1 {
    color: #8CB3D9;
}

/* Friend Codes */
h2 {
    color: #DDDDDD;
}

header h2 span.fc-missing {
    color: #8CB3D9;
}

/* Social Media Links */
header a {
    color: #8CB3D9;
}

header a:hover {
    color: #3F6F9E;
}

/* Loading bar text */
.loading {
    color: #8CB3D9;
}

/* User's name */
header h3 a {
    color: #E9F2FC;
}

header h3 a:hover {
    color: #6290BD;
}

/* User's dex links */
a.link {
    color: #8CB3D9;
}

a.link:hover {
    color: #3F6F9E;
}

/* Edit dex pencil */
.dex-preview-header .dex-edit a.link {
    color: #8CB3D9;
}

.dex-preview-header .dex-edit a.link:hover {
    color: #3F6F9E;
}

/* Grid lines between pokes when selected */
.pokemon.captured .set-captured {
    border-color: #121212;
}

/* Grid lines between pokes */
.set-captured {
    border: 1px solid #121212;
}

/* Box outline */
.box .box-container {
    border: 1px solid #121212;
}

/* Poke's container color */
.pokemon.captured {
    background-color: #103F7C;
}


/* Poke's name text */
h4 {
    color: #8CB3D9;
}

/* Poke's dex number */
.set-captured p {
    color: #8CB3D9;
}

/* Poke's info button */
.set-info .fa-info {
    border: 2px solid #8CB3D9;
    color: #8CB3D9;
}

.pokemon:not(.empty):not(.disabled):hover .set-info:hover .fa-info {
    border: 2px solid #DDDDDD;
    color: #DDDDDD;
}

/* Dex creation form */
.form {
    background-color: #262626;
}

.modal p {
    background-color: #333333;
}

.form-group .fa:last-child {
    color: #8CB3D9;
}

.form-group .radio .radio-custom > span {
    background-color: #103F7C;
}

/* Account settings header*/
.form h1 {
    color: #8CB3D9;
}

/* Account settings label names */
.form-group label {
    color: #DDDDDD;
}

/* Account login text */
form p {
    color: #DDDDDD;
}
}
