css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.garry.tv")
{

p {line-height:1.7em}
body {background-color:#DDDDDD!important;}
div#content {background-color:#DDDDDD!important;}
div#header {background-color:#DDDDDD!important;display:none;}
div#wrap {background-color:#DDDDDD!important;background-image:none!important;}
blockquote {background-color:#666666!important;}
blockquote p
 {color:#EEEEEE!important;
  padding-left:1.5em!important;padding-right:1.5em!important;
  padding-top:0.5em!important;padding-bottom:0.2em!important;
  font-size:0.9em!important;line-height:1.3em!important;
  font-style:italic!important;}
h1 {font-size:4em!important;text-align:center!important;}
h2 {font-size:2.5em!important;text-align:center!important;padding-bottom:1em;}
h3 {background-image:none!important;background-color:#DDDDDD!important;}
div .month {color:#555555!important;font-size:1em!important;}
div .year {color:#555555!important;font-size:2.2em!important;}

div .entrymeta {background-color:#444444!important;}

a {color:#EE5544!important;}

a .qimg {width:200px!important;padding:1em;padding-left:190px;border-color:#DDDDDD!important;
position:relative;}

embed {width:580px!important;height:462px!important;}

div .entrymeta a
{
font-size:1.2em;
padding-right:0.5em;
color:#DD7777!important;
}

div#sidebar h2 {font-size:1.4em!important;text-align:left!important;}
form#searchform {position:relative;top:-2em!important;}
div .entrybody h2 {font-size:1.5em!important;}
}
