css:
@-moz-document regexp("https?://(\\w+\\.|)camelcamelcamel\\.com/.*") {
body {
    background-color: #121212;
}

#h1la{
    filter: brightness(93%) hue-rotate(140deg) saturate(400%) invert(100%);
    -webkit-filter: brightness(93%) hue-rotate(140deg) saturate(400%) invert(100%);
    -moz-filter: brightness(93%) hue-rotate(140deg) saturate(400%) invert(100%);
}

input{
    background-color: #272727 !important;
}

input[type="text"], input[type="password"], #outer_frame * label{
    color: #eaeaea;
}

select{
    background-color: #545454 !important;
    color: #eaeaea;
}

ul > li, div > #outer_frame *, #outer_frame{
    background-color: #4c4c4c;
}
ul.slick-dots > li{
    background-color: #9c9c9c;
}

ul > li > a, ul > li > a *, .pagination a{
    color: #eaeaea;
}

li.menu-text{
    color: #eaeaea !important;
}

ul > li > a:hover, ul > li > a:hover *, h4 *, h3 > a {
    color: #5399fb;
}

a{
    color: #5399fb;
}

ul.breadcrumbs > li{
    background-color: #000;
}

#main_menu_parent, div.bottom{
    background-color: #202020
}

div.bottom * {
    color: #eaeaea;
}

.positive.button, input.button.expanded, .button.large.expanded, [id^="link_"], #allproductsbutton, #dealsonlybutton, .column.small-12.medium-6.medium-text-right > a{
    background: linear-gradient(to bottom, #404040 0%, #202020 99%) !important;
    color: #f24a4a;
}

.button.has-tip.on{
    color: #eaeaea !important;
}

tbody tr:nth-child(even) *, div.card-section{
    background-color: #646464 !important;
    color: #ffffff;
}

tbody tr:nth-child(odd) *, div.card-section.text-center.product_thumb{
    background-color: #484848 !important;
    color: #ffffff;
}

h6 > a {
    color: #6cdfe5;
}

.green{
    color: #0f0 !important;
}

.row.column *, .column-block, .row > .column{
    color: #c6c6c6;
}
}
